(window.webpackJsonp=window.webpackJsonp||[]).push([[115],{487:function(t,v,_){"use strict";_.r(v);var e=_(45),s=Object(e.a)({},(function(){var t=this,v=t.$createElement,_=t._self._c||v;return _("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[_("h1",{attrs:{id:"глава-20-трансцендентная-раса"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#глава-20-трансцендентная-раса"}},[t._v("#")]),t._v(" Глава 20. Трансцендентная Раса")]),t._v(" "),_("p",[t._v('Приступая ко второй части "Бхакти-расамрита-синдху", автор в почтении склоняется перед Санатаной. Слово "Санатана" здесь может относиться как к Самому Шри Кришне, так и к Санатане Госвами, старшему брату и духовному учителю Рупы Госвами. Если под Санатаной подразумевается Шри Кришна, почтение выражается Ему потому, что Он обладает несравненной красотой и некогда убил демона Агху. Если же под Санатаной имеется в виду Санатана Госвами, то Рупа Госвами выражает ему почтение потому, что всегда относился к нему с большой нежностью и служил ему, а также потому, что Санатана Госвами уничтожает все грехи. В этой части "Бхакти-расамрита-синдху" автор намеревается описать общие признаки трансцендентной расы (любовного настроения), испытываемой в преданном служении.')]),t._v(" "),_("p",[t._v('В данном разделе "Бхакти-расамрита-синдху" обсуждаются пять основных тем: 1) вибхава - особые признаки и причины экстаза; 2) анубхава - последующий экстаз; 3) саттвика-бхава - органический, или экзистенциальный, экстаз; 4) вьябхичари-бхава - энергичный экстаз и 5) стхайи-бхава - безудержный, непрерывный экстаз.')]),t._v(" "),_("p",[t._v('Разные люди по-разному понимают слово раса, используемое в "Бхакти-расамрита-синдху". Подобрать ему точный английский эквивалент очень трудно, однако мы видели, что наш духовный учитель переводил слово раса как mellow (Английское слово, означающее "зрелый", "сочный". В русском переводе мы вынуждены были сохранить санскритское слово раса. (Примеч. переводчика.)). Мы последуем его примеру и будем переводить это слово так же.')]),t._v(" "),_("p",[t._v("Расой называют особое любовное настроение, в котором находится преданный, наслаждаясь любовными отношениями с Верховной Личностью Бога. Сочетания различных рас помогают человеку ощутить сладостный вкус преданного служения и благодаря этому испытать высочайшую степень трансцендентного экстаза. Хотя это состояние полностью запредельно нашему опыту, мы, идя по стопам Шрилы Рупы Госвами, постараемся объяснить, что оно собой представляет.")]),t._v(" "),_("p",[t._v('Если деятельность человека не будет приносить ему наслаждения какой-либо расой (любовным настроением), он не сможет продолжать заниматься ею. В сознании Кришны, то есть в трансцендентной жизни, в преданном служении, тоже должна быть своя раса - особый вкус-этого служения. Как правило, ощутить этот вкус позволяют воспевание святых имен, слушание, поклонение в храме и служение Господу. И если человек испытывает трансцендентное блаженство, про него говорят, что он "наслаждается расой". Это значит, что все многообразные ощущения счастья, которые доставляет нам преданное служение, могут быть названы расами преданного служения.')]),t._v(" "),_("p",[t._v("Далеко не каждый в процессе преданного служения может испытать блаженство трансцендентной расы, так как это сладостное любовное состояние возникает только в результате деятельности, которой человек занимался в прошлой жизни, или же благодаря общению с чистыми преданными. Как уже объяснялось, общение с чистыми преданными способно вселить в человека веру в преданное служение. По-настоящему насладиться вкусом преданного служения может лишь тот, кто, общаясь с чистым преданным, обрел такую веру, или тот, кто в прошлой жизни преданно служил Господу. Иначе говоря, обыкновенный человек может испытать это трансцендентное наслаждение, только если ему выпадет чрезвычайная удача находиться в обществе преданных или если он продолжает преданное служение, которым занимался в прошлой жизни.")]),t._v(" "),_("p",[t._v('Постепенный процесс духовного развития, позволяющий возвыситься до преданного служения Господу, описан в Первой песни "Шримад-Бхагаватам": "Все начинается со слушания о Господе Кришне в обществе преданных, которые сами очистили свои сердца посредством должного общения. Слушание повествований о трансцендентных деяниях Господа приведет к тому, что человек будет постоянно ощущать трансцендентное блаженство". В "Бхагавад-гите" тоже объясняется: первым признаком реального достижения духовного уровня является то, что человек все время исполнен радости. Это радостное состояние приходит к нему либо в результате чтения "Бхагавад-гиты" и "Шримад-Бхагаватам", либо в результате общения с людьми, которых очень привлекает духовная жизнь в сознании Кришны, особенно с теми, кто твердо решил снискать благосклонность Говинды, занимаясь трансцендентным любовным служением у Его лотосных стон. У того, кто, воодушевленный таким чувством, постоянно занимается регулируемым преданным служением, стремясь доставить удовольствие Верховной Личности Бога, развивается притяжение к двум трансцендентным категориям, которые подпадают под определение вибхавы. Так преданный наслаждается трансцендентным блаженством.')]),t._v(" "),_("p",[t._v("Необоримая любовь к Кришне может быть вызвана несколькими причинами. Ее источниками могут быть Сам Кришна, преданные Кришны или, например, игра Кришны на флейте. Иногда преданный испытывает благодаря им прилив любви к Кришне, а иногда - застывает, завороженный ими.")]),t._v(" "),_("p",[t._v('Экстатическое состояние характеризуется восемью трансцендентными признаками, проявляющимися в теле преданного. Все эти проявления возможны только в результате смешения пяти упомянутых выше составляющих экстаза. Блаженство экстаза немыслимо без того или иного сочетания этих пяти экстатических начал.  Причина или основа наслаждения трансцендентной расой - это и есть вибхава. Она подразделяется на две категории: основную и побуждающую, дающую импульс. В "Агни-пуране" вибхава описывается так: "Основа, на которой возникает экстатическая любовь, называется вибхавой. Вибхава подразделяется на основную и побуждающую". Иначе говоря, существует два вида экстатической любви. Объектами экстатической любви являются Сам Кришна и Его преданный. Господь Кришна является объектом основной экстатической любви, тогда как Его чистый преданный, исполненный такой любви, является объектом побуждающей экстатической любви. Побуждающая экстатическая любовь - это любовь, развивающаяся в преданном при виде того, что напоминает ему о Кришне.')]),t._v(" "),_("p",[t._v('Господь Кришна, обладающий непостижимыми энергиями и такими свойствами, как трансцендентное знание и блаженство, является основной причиной экстатической любви. Кроме того, принимая формы разных Своих воплощений и экспансий, Господь Кришна становится источником (побудительной причиной) экстатической любви. В "Шримад-Бхагаватам" есть стих, который появляется там в связи с описанием брахма-вимохана-лилы и позволяет получить некоторое представление об этом побудительном, дающем импульс аспекте экстатической любви. Когда Кришна распространил Себя во множество пастушков, телят и коров, тем самым приведя Брахму в замешательство, старший брат Кришны, Шри Баладева (непосредственная экспансия Самого Кришны), изумился: "Удивительно - все эти пастушки, телята и коровы вызывают во Мне ту же экстатическую любовь, которую Я питаю к Кришне!" Эта мысль поразила Его. Это один из примеров того, как Сам Кришна становится объектом и источником экстатической любви в ее побудительном аспекте.')])])}),[],!1,null,null,null);v.default=s.exports}}]);