(window.webpackJsonp=window.webpackJsonp||[]).push([[119],{490:function(v,_,a){"use strict";a.r(_);var t=a(45),s=Object(t.a)({},(function(){var v=this,_=v.$createElement,a=v._self._c||_;return a("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[a("h1",{attrs:{id:"глава-30-продолжение-описания-экстатическои-любви-к-кришне"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#глава-30-продолжение-описания-экстатическои-любви-к-кришне"}},[v._v("#")]),v._v(" Глава 30. Продолжение описания экстатической любви к Кришне")]),v._v(" "),a("h2",{attrs:{id:"смятение"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#смятение"}},[v._v("#")]),v._v(" СМЯТЕНИЕ")]),v._v(" "),a("p",[v._v("В «Хамсадуте» говорится: «Однажды охваченная горем разлуки с Кришной Шримати Радхарани отправилась со Своими подругами на берег Ямуны. Там Радхарани увидела шалаш, где Она много раз предавалась с Кришной любовным утехам, и при воспоминании об этом у Нее закружилась голова. Это головокружение невозможно было скрыть». Это пример смятения, вызванного разлукой.")]),v._v(" "),a("p",[v._v("Есть также описание смятения, вызванного страхом. Его признаки проявились у Арджуны, когда на поле битвы Курукшетра он увидел вселенскую форму Кришны. При этом Арджуну охватило такое смятение, что лук и стрелы выпали у него из рук и он потерял способность отчетливо воспринимать окружающее.")]),v._v(" "),a("h2",{attrs:{id:"помертвение"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#помертвение"}},[v._v("#")]),v._v(" ПОМЕРТВЕНИЕ")]),v._v(" "),a("p",[v._v("Однажды демон по имени Бакасура принял облик огромной цапли и раскрыл клюв, чтобы проглотить Кришну и всех мальчиков-пастушков. Увидев, что Кришна оказался у демона во рту, Баларама и другие пастушки почти лишились чувств и не проявляли никаких признаков жизни. Даже потрясенные каким-то ужасным зрелищем или неожиданным происшествием, преданные ни на мгновение не забывают о Кришне. Они помнят о Кришне даже в моменты величайшей опасности. В этом состоит преимущество сознания Кришны: даже в момент смерти, когда все функции тела нарушаются, в глубине сознания преданный способен помнить о Кришне, и это спасает преданного от возвращения к материальному существованию. Сознание Кришны немедленно переносит человека из материального мира в духовный.")]),v._v(" "),a("p",[v._v("Иллюстрацией тому служит описание смерти жителей Матхуры: «У этих людей слегка участилось дыхание, их глаза широко раскрылись, тело изменило цвет, и они начали шептать святое имя Кришны. В таком состоянии они покинули материальные тела». Таковы признаки преддверия смерти.")]),v._v(" "),a("h2",{attrs:{id:"леность"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#леность"}},[v._v("#")]),v._v(" ЛЕНОСТЬ")]),v._v(" "),a("p",[v._v("Когда самодовольный или нерасположенный к тяжелому труду человек, имея достаточно сил и энергии, уклоняется от выполнения своих обязанностей, его называют ленивым. Леность может проявляться и в экстатической любви к Кришне. Например, когда Махараджа Нанда попросил нескольких брахманов совершить обход холма Говардхана, те ответили, что им больше нравится раздавать благословения, чем обходить холм Говардхана. Это пример лености, вызванной самодовольством.")]),v._v(" "),a("p",[v._v("Однажды, когда Кришна и Его друзья-пастушки затеяли шуточное сражение, Субала стал проявлять признаки усталости. Заметив это, Кришна сказал остальным Своим друзьям: «Субала очень утомился от борьбы со Мной, поэтому больше не докучайте ему просьбами принять участие в сражении». Это пример лености, вызванной нелюбовью к тяжелому труду.")]),v._v(" "),a("h2",{attrs:{id:"неподвижность"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#неподвижность"}},[v._v("#")]),v._v(" НЕПОДВИЖНОСТЬ")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (21.13) гопи восхищаются оцепенением, в которое впали коровы во Вриндаване. Гопи увидели, как коровы заслушались звуками флейты Кришны. Коровы словно пили нектар этих трансцендентных звуков. Телята тоже застыли на месте, забыв о молоке, ждавшем их в коровьем вымени. Казалось, они обнимали Кришну глазами, в которых стояли слезы. Это пример неподвижности, вызванной трансцендентными звуками флейты Кришны.")]),v._v(" "),a("p",[v._v("Услышав оскорбительные слова в адрес Кришны, Лакшмана пришла в такое сильное волнение, что застыла в полной неподвижности, и веки ее окаменели. Это другой пример оцепенения от услышанного.")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (71.39) описывается замешательство царя Юдхиштхиры, с большим почетом принимавшего Кришну у себя во дворце. Присутствие Кришны у него дома доставляло царю такое трансцендентное наслаждение, что он совсем растерялся. Принимая Кришну, царь Юдхиштхира словно впал в забытье. Это пример неподвижности, вызванной экстазом созерцания Кришны.")]),v._v(" "),a("p",[v._v("Аналогичный пример приводится в другом месте Десятой песни «Шримад-Бхагаватам» (39.36). Когда Кришна уезжал в Матхуру, все гопи столпились позади Него. Увидев, что колесница отъезжает, они оцепенели, не в силах сдвинуться с места. Они стояли так до тех пор, пока флаг на колеснице и пыль, поднятая ее колесами, не скрылись из виду.")]),v._v(" "),a("p",[v._v("Как-то один из друзей Кришны обратился к Нему с такими словами: «Дорогой Мукунда [Кришна], от разлуки с Тобой мальчики-пастушки стоят, словно заброшенные Божества в доме профессионального брахмана». Есть профессиональные брахманы, зарабатывающие на жизнь поклонением Божествам. Таких брахманов интересуют не столько Божества, сколько деньги, которые они могут заработать, выдавая себя за святых людей. Поэтому они не украшают как следует Божеств, которым поклоняются, не переодевают и не купают Их, так что Божества выглядят грязными и непривлекательными. На самом деле за Божествами надо тщательно ухаживать: необходимо ежедневно менять Им одежду и украшать Их но мере возможности. Все должно сиять чистотой, чтобы Божества выглядели привлекательно и притягивали к Себе взоры посетителей. Божества в домах профессиональных брахманов упомянуты здесь потому, что являют Собой печальное зрелище. Друзья Кришны, оставшись без Него, стали похожи на таких заброшенных Божеств.")]),v._v(" "),a("h2",{attrs:{id:"смущении"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#смущении"}},[v._v("#")]),v._v(" СМУЩЕНИИ")]),v._v(" "),a("p",[v._v("Когда Радхарани в первый раз представили Кришне, Она очень смутилась. Увидев это, одна из Ее подруг обратилась к Ней с такими словами: «Дорогая подруга, Ты уже продала Себя и Свою красоту Говинде. Какой смысл смущаться? Взгляни же на Него радостно! Продав слона, не торгуются из-за стрекала, которым его погоняют». Такая застенчивость вызвана знакомством в экстатической любви к Кришне.")]),v._v(" "),a("p",[v._v("Индра, владыка рая, побежденный Кришной в бою за обладание цветком париджата, был очень смущен этим и стоял перед Кришной с поникшей головой. Тогда Кришна сказал: «Ну, хорошо, Индра, забирай этот цветок, иначе как ты покажешься на глаза своей жене, Шачидеви?» Смущение Индры было вызвано поражением. В другом случае, когда Кришна стал превозносить достоинства Уддхавы, Уддхава, смущенный Его похвалами, тоже опустил голову.")]),v._v(" "),a("p",[v._v("В «Хари-вамше» упоминается о том, как Сатьябхама, чувствуя себя униженной тем, что Рукмини, — любимая жена Кришны, сказала: «Дорогой Кришна, гора Райватака всегда усыпана весенними цветами, но глаза мои не хотят смотреть на них, ибо я перестала быть желанной для Тебя» Это пример смущения, вызванного поражением.")]),v._v(" "),a("h2",{attrs:{id:"утаивание-чувств"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#утаивание-чувств"}},[v._v("#")]),v._v(" УТАИВАНИЕ ЧУВСТВ")]),v._v(" "),a("p",[v._v("Экстатическая любовь может также проявляться в форме скрытности, то есть в попытках утаить свое истинное состояние, внешне демонстрируя иное отношение к происходящему. Стремясь скрыть свое состояние, человек озирается по сторонам, без особой нужды пытается сделать что-нибудь невозможное или произносит слова, прячущие его настоящие мысли. По мнению ачарьев, в совершенстве знающих людскую психологию, такие попытки скрыть свою истинную любовь — одна из форм проявления экстатических эмоций, которые Кришна возбуждает в преданном.")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (32.15) Шукадева Госвами говорит: «Дорогой царь, обольстительно одетые, с многозначительными улыбками на лицах, гопи всегда выглядели прекрасно. Пытаясь возбудить в Кришне  желание, они то прижимали Его руку к своим коленям, то ставили Его лотосные стопы себе на грудь. После этого они заводили разговор с Кришной, делая вид, что очень сердиты на Него».")]),v._v(" "),a("p",[v._v("Есть другой пример утаивания своих чувств в состоянии экстатической любви. Когда Кришна, лучший из шутников, посадил во дворе у Сатьябхамы дерево париджата, Рукмини, дочь царя Видарбхи, очень рассердилась, но из-за своей природной скромности не подала виду. Никто не мог понять, что у нее в этот момент было на сердце. Это пример утаивания чувств, вызванного соперничеством.")]),v._v(" "),a("p",[v._v("Другой пример утаивания чувств приводится в Первой песни «Шримад-Бхагаватам» (11,32). Когда Кришна возвратился в Двараку, разные члены Его семьи по-разному встретили Его. Еще издали увидев своего мужа, царицы Двараки поспешили мысленно обнять Его, а затем окинули Его взглядом. Когда же Кришна подошел ближе, они подтолкнули вперед сыновей, чтобы те обняли Его. Другие, стесняясь, старались удержаться от слез, но слезы все равно катились у них по щекам. Это пример утаивания чувств, вызванного застенчивостью.")]),v._v(" "),a("p",[v._v("Как-то раз, когда Шримати Радхарани решила, что Кришна встречается с другой женщиной, Она призналась подруге: «Дорогая подруга, стоит Мне подумать, что пастушок Кришна привязан к другой женщине, как Меня охватывает страх, а волосы на теле встают дыбом. Мне нужно быть очень осторожной, чтобы не попасться Ему на глаза в такой момент». Это пример утаивания чувств, вызванного застенчивостью и расчетливостью.")]),v._v(" "),a("p",[v._v("Утверждается, что «хотя Шримати Радхарани питала сильную любовную привязанность к Кришне, Она прятала ее в самой глубине сердца, чтобы скрыть Свое состояние от глаз посторонних». Это пример утаивания чувств, вызванного скромностью.")]),v._v(" "),a("p",[v._v("Однажды Кришна принялся непринужденно шутить со Своими друзьями-пастушками. Присутствовавший при этом слуга Кришны, Патри, тоже наслаждался их дружеской беседой. Однако, вспомнив о своем положении, Патри почтительно поклонился своему господину и, совладав с собой, подавил улыбку. Эта спрятанная улыбка — пример утаивания чувств, вызванного уважительным отношением.")]),v._v(" "),a("h2",{attrs:{id:"памятование"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#памятование"}},[v._v("#")]),v._v(" ПАМЯТОВАНИЕ")]),v._v(" "),a("p",[v._v("Воспоминания о Кришне могут вызвать в преданном многочисленные проявления экстатической любви. К примеру, один из друзей Кришны рассказывал Ему: «Дорогой Мукунда, стоило лотосоокой Радхарани увидеть на небе темную тучу, как Она тотчас вспомнила о Тебе. Один вид этой тучи вселил в Нее желание снова увидеться с Тобой». Это пример того, как предмет, чем-то похожий на Кришну, вызывает в преданном, который исполнен экстатической любви к Кришне, воспоминание о Нем. Цвет тела Кришны близок к темно-синему оттенку тучи, поэтому Шримати Радхарани было достаточно увидеть тучу, чтобы вспомнить о Кришне.")]),v._v(" "),a("p",[v._v("Один преданный признался, что порой даже в минуты рассеянности воспоминания о лотосных стопах Кришны сами собой, словно у сумасшедшего, всплывают в его сердце. Это пример памятования, вызванного постоянной практикой. Иначе говоря, у преданных, всегда думающих о лотосных стопах Кришны, образ Господа Кришны сам собой возникает в сердце, даже если их внимание на какое-то мгновение отвлекается.")]),v._v(" "),a("h2",{attrs:{id:"склонность-к-рассуждениям"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#склонность-к-рассуждениям"}},[v._v("#")]),v._v(" СКЛОННОСТЬ К РАССУЖДЕНИЯМ")]),v._v(" "),a("p",[v._v("Мадхумангала, близкий друг Кришны, принадлежал к сословию брахманов. Друзья Кришны в большинстве своем были пастухами из сословия вайшьев, но среди них были и брахманы. Во Вриндаване живут, главным образом, два сословия: вайшьи и брахманы. Как-то раз Мадхумангала сказал Кришне: «Дорогой друг, я обратил внимание на то, что Ты не замечаешь павлиньих перьев, падающих на землю, и не смотришь на подаренные Тебе гирлянды. Мне кажется, что я догадываюсь о причине Твоей рассеянности, когда вижу, как Твои глаза, словно два черных шмеля, летящих на цветок лотоса, устремляются навстречу глазам Шримати Радхарани». Это пример предположения, вызванного экстатической любовью.")]),v._v(" "),a("p",[v._v("Однажды, увидев прогуливавшегося Кришну, одна из близких подруг Радхарани сказала Ей: «Дорогая подруга, не кажется ли Тебе, что тот, кто гуляет там, — дерево тамала? Впрочем, если это дерево тамала, то как оно может гулять и быть таким прекрасным? Тогда, может быть, это туча? Но если это туча, то где скрываемая ею прекрасная луна? Остается допустить, что это не кто иной, как пленительный Верховный Господь, покоряющий все три мира звуками Своей флейты. Должно быть, это Сам Мукунда, стоящий у подножия холма Говардхана». Это еще один пример рассуждений в экстатической любви.")]),v._v(" "),a("h2",{attrs:{id:"беспокоиство"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#беспокоиство"}},[v._v("#")]),v._v(" БЕСПОКОЙСТВО")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (29.29) говорится, что, когда Кришна попросил гопи возвращаться по домам, им это не понравилось. Огорченные, они тяжело задышали, а их прекрасные лица, казалось, потухли. Гопи стояли, не произнося ни звука. Большими пальцами ног они принялись чертить узоры на земле, и слезы смывали сурьму, которой были подведены их глаза, и капали им на грудь, посыпанную кункумой. Это пример беспокойства в экстатической любви.")]),v._v(" "),a("p",[v._v("Один друг Кришны сказал Ему: «О победитель демона Муры, Твоя добрая и ласковая мать очень волнуется, оттого что Ты до сих пор не вернулся домой. С тяжелым сердцем она просидела весь вечер на балконе. Поразительно, как Ты мог позабыть о Своей матери, заигравшись где-то!» Это другой пример сильного беспокойства в экстатической любви.")]),v._v(" "),a("p",[v._v("Когда матушка Яшода с беспокойством ожидала возвращения Кришны из Матхуры, Махараджа Нанда утешал ее: «Дорогая Яшода, пожалуйста, не волнуйся. Утри слезы со своего прекрасного, как лотос, лица. Не надо так горячо дышать. Я немедленно поеду с Акрурой во дворец Камсы и верну тебе сына». Это пример беспокойства в экстатической любви, вызванного опасной ситуацией, в которой оказался Кришна.")]),v._v(" "),a("h2",{attrs:{id:"вдумчивость"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#вдумчивость"}},[v._v("#")]),v._v(" ВДУМЧИВОСТЬ")]),v._v(" "),a("p",[v._v("В «Вайшакха-махатмье» (одном из разделов «Падма-пураны») преданный утверждает, что в некоторых из восемнадцати Пуран ничего не говорится о прославлении Господа Вишну, а вместо этого рекомендуется миллионы лет прославлять тех или иных полубогов. Однако при более тщательном изучении Пуран становится ясно, что Верховной Личностью Бога является Господь Вишну. Это пример экстатической любви, являющейся плодом размышлений.")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (60.39) Рукминидеви так выражает свою необычайную привязанность к Кришне: «Дорогой Господь Кришна, великие мудрецы, свободные от материальной скверны, воспевают Твое трансцендентное величие. В ответ на это Ты по Своей доброте безвозмездно отдаешь им Себя. Одной Твоей милости достаточно, чтобы человек мог возвыситься; точно так же, стоит Тебе захотеть, и удача, послушная воле вечного времени, отвернется от человека. Поэтому я избрала Тебя, о Господь, своим мужем, отвергнув даже Брахму и Индру, не говоря уже об остальных». Рукмини развила в себе любовь к Кришне, просто размышляя о Нем. Это пример вдумчивости в экстатической любви.")]),v._v(" "),a("h2",{attrs:{id:"невозмутимость"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#невозмутимость"}},[v._v("#")]),v._v(" НЕВОЗМУТИМОСТЬ")]),v._v(" "),a("p",[v._v("Состояние полного удовлетворения, приходящее к человеку, который обрел знание, возвысился над всеми страданиями или достиг желанной цели жизни в трансцендентном преданном служении Богу, называется дхрити — невозмутимостью, или душевным равновесием. В этом состоянии никакие потери не способны вызвать у человека смятение и ему кажется, что он достиг всего, чего только можно достичь.")]),v._v(" "),a("p",[v._v("По мнению Бхартрихари, большого знатока священных писаний, человек, обретший невозмутимость, думает так: «Меня не прельщает высокий пост в правительстве. Я скорее предпочту ходить обнаженным, не иметь никакой одежды и спать на голой земле. Никакие неудобства не заставят меня наняться на службу к кому-то, даже к правителю». Иначе говоря, обретя экстатическую любовь к Личности Бога, человек готов терпеливо сносить все, что с материальной точки зрения является неудобствами.")]),v._v(" "),a("p",[v._v("Махараджа Нанда, отец Кришны, думал: «На моих пастбищах поселилась сама богиня процветания, у меня более миллиона коров, пасущихся повсюду. Мало того, у меня есть такой сын, как Кришна, сильный и неутомимый работник. Поэтому, несмотря на то что я семейный человек, я очень доволен жизнью!» Это пример невозмутимости, порожденной отсутствием неприятностей.")]),v._v(" "),a("p",[v._v("Другой преданный сказал однажды: «Я всегда плаваю в нектарном океане игр Личности Бога, и потому ни религиозные обряды, ни экономическое процветание, ни чувственные наслаждения, ни, в конце концов, освобождение — слияние с бытием Брахмана — больше не привлекают меня». Это пример невозмутимости, приходящей в результате достижения самого лучшего, что есть в этом мире. Лучшее, что есть в этом мире, — это погруженность в сознание Кришны.")]),v._v(" "),a("h2",{attrs:{id:"счастье"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#счастье"}},[v._v("#")]),v._v(" СЧАСТЬЕ")]),v._v(" "),a("p",[v._v("В «Вишну-пуране» рассказывается о том, как Акрура приехал во Вриндаван, чтобы забрать Кришну и Балараму в Матхуру. Стоило ему увидеть Их лица, как его охватила такая радость, что все его тело украсилось признаками экстатической любви. Такое состояние называют счастьем.")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (33.11) говорится: «Почувствовав во время танца раса руку Кришны на своем плече, одна из гопи ощутила такой прилив экстатического счастья, что поцеловала Кришну». Это пример счастья, вызванного достижением желанной цели.")]),v._v(" "),a("h2",{attrs:{id:"страстное-желание"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#страстное-желание"}},[v._v("#")]),v._v(" СТРАСТНОЕ ЖЕЛАНИЕ")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (71.33) говорится: «Когда Кришна впервые приехал из Своего царства, Двараки, в Индрапрастху, молодые жительницы города до такой степени загорелись желанием увидеть Его, что даже ночью, лежа в постелях со своими мужьями, не могли сдержать этого стремления. Одетые кое-как, непричесанные, оставив все домашние дела, они выбежали на улицу, чтобы увидеть Кришну». Это пример страстного желания в экстатической любви.")]),v._v(" "),a("p",[v._v("В «Стававали» Шри Рагхунатха дас Госвами молит о милости Радхарани, которую так пленили звуки флейты Кришны, что, едва заслышав их, Она принялась расспрашивать обитателей вриндаванского леса, где Кришна. Когда Радхарани впервые увидела Кришну, Ее охватила такая экстатическая любовь и такое блаженство, что Она принялась поглаживать Свои уши. Радхарани и другие девушки Враджа были мастерицами вести хитрые разговоры. Едва завидев Кришну, они начинали такие разговоры, и Кришна, притворяясь, что отправился за цветами для них, немедленно уходил оттуда в горную пещеру. Это другой пример страстного стремления к любовному общению со стороны как гопи, так и Кришны.")]),v._v(" "),a("h2",{attrs:{id:"ярость"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#ярость"}},[v._v("#")]),v._v(" ЯРОСТЬ")]),v._v(" "),a("p",[v._v("Когда Кришна сражался с со змеем Калией, танцуя на его головах, тот укусил Кришну за ногу. Увидев это, Гаруда пришел в ярость и начал бормотать: «Кришна обладает такой силой, что от одного звука Его громового голоса у жен Калии случились выкидыши. Будь на то моя воля, я сейчас же проглотил бы этого змея за то, что он оскорбил моего Господа. Однако я не стану делать этого в присутствии Господа, потому что Он может рассердиться на меня». Это пример страстного желания действовать в экстатической любви при виде непочтительного отношения к Кришне.")]),v._v(" "),a("p",[v._v("Когда Шишупала стал возражать против того, чтобы Кришне поклонялись на арене жертвоприношения раджасуя, устроенного Махараджей Юдхиштхирой, Сахадева, младший брат Арджуны, сказал: «Человек, не терпящий, когда другие поклоняются Кришне, — мой враг. Этот человек демоничен по природе, поэтому меня так и тянет ударить левой ногой по его большой голове, чтобы наказать его строже, чем жезл Ямараджи!» Баладева тоже возмутился: «Да будет благословен Господь Кришна! Я поражен тем, как недостойные потомки рода Куру, узурпировавшие трон царства Куру, позволяют себе поносить Кришну, прибегая к дипломатическим уловкам. О, это невыносимо!» Это еще один пример жажды действий, возникшей при виде непочтительного отношения к Кришне.")]),v._v(" "),a("h2",{attrs:{id:"высокомерие-приводящее-к-поношениям"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#высокомерие-приводящее-к-поношениям"}},[v._v("#")]),v._v(" ВЫСОКОМЕРИЕ, ПРИВОДЯЩЕЕ К ПОНОШЕНИЯМ")]),v._v(" "),a("p",[v._v("В «Видагдха-Мадхавс» рассказывается, как свекровь Радхарани, Джатила, принялась упрекать Кришну: «Кришна, Ты стоишь здесь, а Радхарани, недавно вышедшая замуж за моего сына, стоит рядом с Тобой. Теперь мне все про Вас известно. Как же мне не хотеть оградить свою сноху от Твоих пляшущих глаз?» Это пример бесчестящих слов, цель которых — косвенно упрекнуть Кришну.")]),v._v(" "),a("p",[v._v("Однажды несколько гопи стали отчитывать Кришну: «Дорогой Кришна, Ты самый настоящий вор, поэтому уходи отсюда немедленно. Мы знаем, что Ты любишь Чандравали сильнее, чем нас, но Ты ничего не добьешься, расхваливая ее в нашем присутствии! Пожалуйста, не пачкай здесь имя Радхарани!» Это другой пример бесчестящих слов, брошенных Кришне в экстатической любви.")]),v._v(" "),a("p",[v._v("Еще один подобный пример приводится в Десятой песни «Шримад-Бхагаватам» (31.16). Гопи убежали из своих домов, чтобы встретиться с Кришной во вриндаванском лесу, но Кришна отверг их и велел отправляться по домам, прочитав им нравоучения. На это гопи ответили: «Дорогой Кришна, разлука с Тобой причиняет нам невыносимые страдания, а свидание с Тобой приносит неописуемое счастье. Поэтому мы побросали своих мужей, братьев, остальных родственников, подруг и пришли к Тебе, завороженные звуками Твоей трансцендентной флейты. О непогрешимый, да будет Тебе известна причина нашего прихода. Откровенно говоря, мы здесь лишь потому, что нас пленили сладостные звуки Твоей флейты. Мы хороши собой, а Ты так глуп, что отвергаешь наше общество. Только Ты способен на такую глупость — не воспользоваться возможностью свидания с молодыми женщинами глухой ночью!» Это поношение Кришны тоже является косвенным упреком в экстатической любви.")]),v._v(" "),a("h2",{attrs:{id:"зависть"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#зависть"}},[v._v("#")]),v._v(" ЗАВИСТЬ")]),v._v(" "),a("p",[v._v("В «Падьявали» приводятся обращенные к Радхарани слова одной из Ее подруг: «Дорогая подруга, пожалуйста, не очень-то гордись тем, что Кришна собственноручно украсил Твой лоб. Вполне возможно, что Он влюблен в какую-нибудь другую красавицу. Я вижу, что узоры на Твоем челе сделаны очень аккуратно: похоже, Кришна не слишком волновался, когда разукрашивал его, иначе Он не смог бы провести такие безупречные линии!» Это пример зависти, которая вызвана удачей, выпавшей Радхе.")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (30.30) есть такие слова: «Разыскивая Кришну и Радху перед танцем раса, гопи переговаривались между собой: „Мы видели отпечатки стоп Кришны и Радхи на земле Вриндавана, и это причиняет нам невыносимую боль, ибо Кришна для нас все, а эта красавица коварно похитила Его у нас и одна наслаждается Его поцелуями, не думая делиться Им с нами!“». Это еще один пример зависти к счастливому жребию Шримати Радхарани.")]),v._v(" "),a("p",[v._v("Иногда в играх пастушков в лесах Вриндавана Кришна принимал одну сторону, а Баларама — другую. Они вступали в шуточное сражение, и, когда победу одерживала команда Баларамы, мальчики говорили: «Раз уж победа досталась команде Баларамы, едва ли в целом свете найдется кто-то слабее нас!» Это другой пример зависти в экстатической любви.")]),v._v(" "),a("h2",{attrs:{id:"дерзость"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#дерзость"}},[v._v("#")]),v._v(" ДЕРЗОСТЬ")]),v._v(" "),a("p",[v._v("В Десятой песни «Шримад-Бхагаватам» (52.41) Рукмини пишет Кришне: «О непобедимый Кришна, моя свадьба назначена на завтра. Прошу Тебя: тайно прибудь в Видарбху и вели Своим солдатам и полководцам внезапно окружить и разгромить войска царя Магадхи. Так, прибегнув к методу демонов, похить меня и женись на мне».")]),v._v(" "),a("p",[v._v("Согласно ведическим обычаям, существует восемь способов женитьбы. Один из них называется ракшаса-виваха. Когда, чтобы жениться на девушке, ее похищают, это называется ракшаса-вивахой. Этот способ считается демоническим. По выбору своего старшего брата Рукмини должна была выйти замуж за Шишупалу, поэтому она отправила Кришне письмо с просьбой похитить ее. Это пример дерзости в экстатической любви к Кришне.")]),v._v(" "),a("p",[v._v("Одна из гопи сказала: «Пусть же волны Ямуны унесут в океан сладкогласую флейту Кришны! Волшебный звук этой флейты столь дерзок, что лишает нас самообладания на глазах у старших».")]),v._v(" "),a("h2",{attrs:{id:"головокружение"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#головокружение"}},[v._v("#")]),v._v(" ГОЛОВОКРУЖЕНИЕ")]),v._v(" "),a("p",[v._v("Каждый вечер на заходе солнца Кришна возвращался из леса, где пас коров. Временами, когда матушка Яшода не слышала сладких звуков Его флейты, она приходила в необычайное волнение и от этого у нее начинала кружиться голова. Таким образом, волнение в экстатической любви к Кришне может приводить к головокружению.")]),v._v(" "),a("p",[v._v("Однажды, связав Кришну, Яшода подумала: «Тело моего Кришны такое нежное и мягкое. Как я могла связать Его веревкой?» От этих мыслей в голове у нее все смешалось, и она почувствовала головокружение.")]),v._v(" "),a("p",[v._v("Старшие советовали гопи запирать ночью двери на засовы, но те были так беззаботны, что не очень строго следовали их совету. Иногда мысли о Кришне придавали им такую уверенность в своей безопасности, что они ложились спать во дворах своих домов. Это пример головокружения в экстатической любви, вызванного врожденной привязанностью к Кришне.")]),v._v(" "),a("p",[v._v("Кто-то может спросить, почему у преданных Кришны бывает головокружение, которое обычно считают признаком гуны невежества. Отвечая на этот вопрос, Шри Джива Госвами объясняет, что преданные Господа Кришны всегда трансцендентны любым гунам материальной природы. Когда у них кружится голова или когда они ложатся спать, считается, что они пребывают не под влиянием гун природы, а в трансе преданного служения. В «Гаруда-пуране» есть авторитетное утверждение, касающееся йгов-мистиков, которые находятся под непосредственным покровительством Верховной Личности Бога: «Во всех трех состояниях сознания — бодрствовании, дремоте и глубоком сне — преданные всегда поглощены мыслями о Верховной Личности Бога. Следовательно, полностью поглощенные мыслями о Кришне, преданные не спят».")]),v._v(" "),a("h2",{attrs:{id:"сонливость"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#сонливость"}},[v._v("#")]),v._v(" СОНЛИВОСТЬ")]),v._v(" "),a("p",[v._v("Однажды Господь Баладева так заговорил во сне: «О лотосоокий Кришна, все Твои детские приключения происходят лишь по Твоей воле. Так сокруши скорее упрямую гордыню змея Калии». Эти слова Господа Баладевы удивили и рассмешили собравшихся Ядавов. А Господь Баладева, носитель плуга, зевнул так широко, что на Его животе появились складки, и снова погрузился в глубокий сон. Это пример сонливости в экстатической любви.")]),v._v(" "),a("h2",{attrs:{id:"пробуждение"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#пробуждение"}},[v._v("#")]),v._v(" ПРОБУЖДЕНИЕ")]),v._v(" "),a("p",[v._v("Некий преданный сказал однажды: «Я победил гуну невежества, обретя трансцендентное знание. Отныне единственным моим занятием будут поиски Верховной Личности Бога». Это пример пробуждения в экстатической любви. Трансцендентное пробуждение возможно только в том случае, когда человек полностью преодолел влияние иллюзии. Достигнув этого уровня, преданный в любом проявлении материальных элементов — звуке, запахе, прикосновении, вкусе — ощущает трансцендентное присутствие Верховной Личности Бога. В таком состоянии преданный постоянно проявляет признаки экстаза (например, волосы на его теле встают дыбом, он начинает вращать глазами или пробуждается ото сна).")]),v._v(" "),a("p",[v._v("Впервые увидев Кришну, Шримати Радхарани внезапно во всей полноте познала трансцендентное счастье, и от этого все органы Ее тела замерли. Но стоило Лалите, Ее неизменной спутнице, прошептать Ей на ухо святое имя Кришны, как Радхарани тут же широко открыла глаза. Это пример пробуждения, вызванного звуком имени Кришны.")]),v._v(" "),a("p",[v._v("Однажды в шутливом настроении Кришна сообщил Радхарани: «Дорогая Радхарани, Я собираюсь покинуть Тебя». Промолвив это, Он тут же исчез, а Радхарани пришла в такое отчаяние, что цвет Ее тела изменился и Она, как подкошенная, упала на землю Вриндавана, Ее дыхание почти остановилось. Однако, вдохнув аромат росших на земле цветов, Она очнулась в экстатическом состоянии и поднялась. Это пример трансцендентного пробуждения, вызванного запахом.")]),v._v(" "),a("p",[v._v("Когда Кришна прикоснулся к телу одной из гопи, та спросила у своей подруги: «Дорогая подруга, чья это рука касается моего тела? Темный лес на берегу Ямуны очень испугал меня, но неожиданное прикосновение этой руки спасло меня от истерики». Это пример ясности сознания, обретенной благодаря прикосновению.")]),v._v(" "),a("p",[v._v("Одна гопи сказала Кришне: «О Кришна, когда Ты покинул место, где проходил танец раса, наша самая дорогая подруга, Радхарани, тут же упала на землю, лишившись чувств. Но когда я поднесла Ей немного бетеля, который Ты жевал, Она мгновенно пришла в сознание, и признаки ликования проявились в Ее теле». Это пример пробуждения, вызванного ощущением вкуса.")]),v._v(" "),a("p",[v._v("Однажды ночью Шримати Радхарани говорила во сне: «Дорогой Кришна, пожалуйста, не подшучивай больше надо Мной! Пожалуйста, не надо! И не дотрагивайся больше до Моих одежд. А иначе Я все расскажу старшим, и все узнают о Твоих проделках». Сказав это во сне, Она вдруг проснулась и увидела, что рядом стоят Ее старшие родственники. Смутившись, Она наклонила голову. Это пример того, как преданный приходит в себя, очнувшись от сна.")]),v._v(" "),a("p",[v._v("Тому есть и другие примеры. Посланец Кришны, придя к Шримати Радхарани, застал Ее спящей, но Она сразу проснулась. Точно так же, когда поздней ночью Кришна начинал играть на флейте, все гопи, прекрасные дочери пастухов, мгновенно просыпались. В связи с этим было сделано одно очень красивое сравнение: «Иногда лотос окружают белые лебеди, а иногда вокруг него вьются черные осы, собирающие мед. Когда в небе гремит гром, лебеди уплывают, но черные осы продолжают наслаждаться лотосом». Сон гопи сравнивают здесь с белыми лебедями, а звучание флейты Кришны — с черной осой. Когда звучала флейта Кришны, белые лебеди — сон гопи — сразу уплывали, а черная оса звучания флейты наслаждалась лотосом красоты гопи.")])])}),[],!1,null,null,null);_.default=s.exports}}]);