(window.webpackJsonp=window.webpackJsonp||[]).push([[120],{493:function(v,_,p){"use strict";p.r(_);var t=p(45),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"глава-31-допольнительные-признаки-экстатическои-любви-к-кришне"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#глава-31-допольнительные-признаки-экстатическои-любви-к-кришне"}},[v._v("#")]),v._v(" Глава 31. Допольнительные признаки экстатической любви к Кришне")]),v._v(" "),p("p",[v._v("Тридцать три перечисленных выше признака экстатической любви называются вьябхичари — «выводящие из равновесия». Все эти признаки относятся к состояниям, в которых человек как будто выведен из равновесия, однако сильная экстатическая любовь к Кришне проявляется даже в таких состояниях. Эти признаки можно разделить на три группы, отнеся их к первому, второму и третьему классам. Признаки экстатической любви, выводящей человека из равновесия, многообразны: зависть, обеспокоенность, встревоженность, гордость, ревность, задумчивость, малодушие, снисходительность, нетерпение, страстное желание, сожаление, сомнения и дерзость. Все они входят в число тридцати трех состояний, в которых проявляется экстатическая любовь. Шрила Рупа Госвами тщательно проанализировал различные виды выводящих из равновесия проявлений экстатической любви, и, хотя в английском языке очень трудно подобрать точные эквивалентны многим санскритским терминам, мы сейчас изложим сделанные им заключения.")]),v._v(" "),p("p",[v._v("Когда чужие успехи вызывают у человека недобрые чувства, это состояние обычно называют завистью. Когда человек пугается молнии в небе, этот страх вселяет в него тревогу. Следовательно, страх и встревоженность можно объединить. Стремление скрыть свои истинные чувства и мысли называется авахиттхой — утаиванием, а желание показать свое превосходство — гордостью. Их можно объединить в понятие притворства. Притворство включает в себя как авахттху, так и гордость. Когда человек не может сносить оскорбления, это называется амаршей, а когда он не может спокойно относиться к богатству другого — ревностью. Причиной и того и другого является нетерпимость. Способность человека обосновать правильное понимание какого-либо слова называют убедительностью. Прежде чем вынести окончательное решение о смысле слова, необходимо тщательно его обдумать. Стало быть, формирование убедительного вывода происходит в процессе обдумывания. Когда человек отзывается о себе как о невежде, это называют скромностью, а отсутствие решительности называют малодушием. Иногда скромность включает в себя малодушие. Когда человека трудно вывести из равновесия, это качество называют терпимостью. Способность спокойно сносить оскорбления также называют терпимостью. Таким образом, снисходительность и терпимость могут быть синонимами. Когда человек подгоняет время, его называют нетерпеливым, а когда он видит что-то чудесное, про него говорят, что он изумлен. Нетерпение может быть вызвано изумлением, так что нетерпеливость и изумление тоже могут быть синонимами. Тревога в дремлющем состоянии проявляется в форме страстного желания, поэтому тревога и страстное желание также могут быть синонимами. Чувство человека, охваченного раскаянием в своем проступке, называют стыдом. Следовательно, стыд и раскаяние могут быть синонимами. Сомнения — одно из проявлений склонности к рассуждениям. Проявив дерзость, человек начинает беспокоиться, поэтому беспокойство и дерзость тоже можно считать синонимами.")]),v._v(" "),p("p",[v._v("Когда все эти признаки проявляются в экстатической любви, их называют санчари, то есть постоянными признаками экстаза. Все они трансцендентны и в зависимости от обстоятельств могут проявляться по-разному — по отдельности или взаимодействуя с другими признаками. Это напоминает обмен знаками внимания между влюбленными.")]),v._v(" "),p("p",[v._v("От зависти или стыда у человека порой меняется цвет тела. Это можно назвать вибхавой, то есть вторичным проявлением экстаза. Иногда к вибхаве относят также обман чувств, упадок сил и сильное беспокойство. Когда одновременно присутствует много таких признаков, это просто можно считать проявлением экстатической любви.")]),v._v(" "),p("p",[v._v("Шрила Рупа Гостами говорит, что к постоянным признакам экстатической любви иногда причисляют боязнь, сон, усталость, лень и безумие, вызванное опьянением. Эти признаки свидетельствуют о сильной привязанности.")]),v._v(" "),p("p",[v._v("Экстатическая любовь может проявляться и в форме необоснованных споров, решительности, уравновешенности, памятования, радости, невежества, скромности и потери сознания. К проявлениям экстатической любви относят также зависимость, которая, в свою очередь, подразделяется на зависимость с чувством превосходства и зависимость в подчинении. Рупа Госвами установил, чем конкретно отличается зависимость с чувством превосходства от зависимости в подчинении, и ниже это будет объяснено.")]),v._v(" "),p("p",[v._v("Один преданный воскликнул: «Увы, я не вижу землю Матхуры! Стоит мне услышать это название, „Матхура“, как волосы на моем теле встают дыбом, и тем не менее я лишен возможности видеть ее. Для чего мне тогда глаза?» В этих словах выражено сильное желание увидеть Матхуру и ее окрестности, вызванное глубокой привязанностью к Кришне. А Бхима выразил свою сильную привязанность к Кришне, когда стал бормотать: «Мои руки могучи, как молнии, но что в них проку, если этими могучими руками я не смог уничтожить Шишупалу, когда тот поносил Кришну?» Бхима был очень разгневан, и вызванное гневом отчаяние пробудило в нем сильную привязанность к Кришне. Это пример того, как сильная привязанность к Кришне проявляется в приступе гнева.")]),v._v(" "),p("p",[v._v("Когда Арджуна увидел вселенскую форму Кришны, ослепительные зубы которой на его глазах перемалывали вселенную, во рту у него пересохло. И хотя он всегда полагался на милость Кришны, в тот момент Арджуна забыл, что он друг Кришны. Это пример проявления подчиненной зависимости.")]),v._v(" "),p("p",[v._v("Иногда поводом для пробуждения сильной экстатической любви к Кришне могут служить и ужасные поступки. Состояние ума при этом называют экстатическим страхом в иллюзии. В Десятой песни «Шримад-Бхагаватам» (23.40) приведены слова брахманов, совершавших жертвоприношения: «Наше происхождение дало нам три преимущества: принадлежность к благородным семьям брахманов, возможность пройти обряд надевания священного шнура и получить посвящение от духовного учителя. Увы, все эти преимущества обернулись проклятьем. Проклятье лежит даже на данном нами обете брахмачарьи». Так брахманы проклинали всё, чем они занимались. Они осознали, что, несмотря на очень хорошее происхождение, образование и воспитание, по-прежнему находятся во власти иллюзорной энергии. Они также признали, что даже великие йоги, если они не преданы Господу, находятся под влиянием материальной энергии. То отчаяние, которое ощутили брахманы, совершавшие ведические обряды, свидетельствовало о практически полном отсутствии у них привязанности к Кришне. Но бывает отчаяние, в котором проявляется привязанность к Кришне. Когда демон, обернувшийся быком, набросился на девушек Враджа, они закричали: «О Кришна, спаси нас! Мы пропали!» Это пример отчаяния с привязанностью к Кришне.")]),v._v(" "),p("p",[v._v("Когда Кришна убил демона Кеши, Камсу тоже охватило отчаяние. Он думал: «Кеши-дайтья был мне дороже жизни, но его убил простой пастух — неотесанный, необразованный и ничего не смыслящий в боевом искусстве. Я без труда одержал победу над царем небес, но, похоже, так и не понял, в чем смысл жизни». В этом отчаянии есть оттенок привязанности к Кришне, и потому его считают отражением экстатической любви в отчаянии.")]),v._v(" "),p("p",[v._v("Однажды Камса выговаривал Акруре: «Ты так глуп, что принимаешь обыкновенного пастуха за Верховную Личность Бога только потому, что Он победил какого-то безобидного водяного змея! Может, этот мальчик и поднял камешек под названием холм Говардхана, но даже это не так удивительно, как то, что ты называешь Его Личностью Бога!» В этом случае отчаяние как отражение экстатической любви к Кришне проявилось во враждебном отношении к Нему.")]),v._v(" "),p("p",[v._v("Пытаясь утешить дерево кадамба, горевавшее о том, что Кришна не прикоснулся даже к его тени, один преданный сказал: «Дорогое дерево кадамба, успокойся. Расправившись со змеем Калией, поселившимся в Ямуне, Кришна придет к тебе и исполнит твое желание». Это пример необоснованного отчаяния, которое является отражением экстатической любви к Кришне.")]),v._v(" "),p("p",[v._v("Однажды Гаруда (орел, носящий Вишну) сказал: «Кто на свете чище меня? Где найти другую такую могучую и искусную птицу? Кришна может недолюбливать меня, Ему может не нравиться мое общество, и тем не менее Он вынужден пользоваться моими крыльями!» Это пример отчаяния как отражения нейтральной экстатической любви.")]),v._v(" "),p("p",[v._v("Иногда признаки экстатической любви объединяют в четыре группы, которые называются «зарождение», «сочетание», «объединение» и «удовлетворение».")]),v._v(" "),p("p",[v._v("Однажды Кришна сказал Радхарани: «Дорогая Радхарани, когда утром Ты пыталась встретиться со Мной наедине, Твоя подруга Мекхала от зависти не могла есть. Только посмотри на нее!» После этой шутки Кришны Радхарани нахмурила Свои прекрасные брови. Рупа Госвами молит о том, чтобы на всех снизошла благодать этого движения бровей Шримати Радхарани. Это пример того, как в экстатической любви к Кришне зарождается гнев.")]),v._v(" "),p("p",[v._v("Однажды ночью, после того как младенец Кришна убил демоницу Путану, Его увидели играющим на ее груди. От этого зрелища Яшода оцепенела и некоторое время не могла прийти в себя. Это пример сочетания нескольких признаков экстатической любви. Такое сочетание может быть благоприятным и неблагоприятным. Благоприятным обстоятельством было то, что демоница Путана мертва, а неблагоприятным — то, что Кришна играет на ее груди глубокой ночью и вокруг нет никого, кто мог бы прийти Ему на помощь, случись с Ним что-нибудь. Яшода была застигнута врасплох этим сочетанием благоприятного и неблагоприятного.")]),v._v(" "),p("p",[v._v("Когда Кришна только научился ходить, Он очень часто выходил из дома. Яшода была немало удивлена этим и заметила: «Этот ребенок до того непоседлив, что за Ним не уследишь! Он все время гуляет по Гокуле [Вриндавану], а затем возвращается домой. Я вижу, что Он ничего не боится, но сама все больше боюсь, не случилась бы с Ним какая-нибудь беда». Это другой пример противоречивого сочетания эмоций: ребенок ничего не боится, а Яшода опасается беды. В данном случае опасность — это причина, а переживания Яшоды представляют собой сочетание двух противоречивых признаков экстатической любви. Иначе говоря, Яшода чувствовала одновременно и счастье, и тревогу, точнее усиливающийся страх.")]),v._v(" "),p("p",[v._v("Увидев своего ликующего сына в окружении борцов на арене жертвоприношения, устроенного Камсой, Деваки, мать Кришны, почувствовала, как по ее щекам потекли слезы двух видов: временами слезы были теплыми, а временами — холодными. Это пример сочетания ликования и горя в экстатической любви, вызванных разными причинами.")]),v._v(" "),p("p",[v._v("Однажды Шримати Радхарани стояла на берегу Ямуны в лесу Вриндавана. В это время на Нее внезапно набросился Кришна, который был сильнее Ее. И хотя Она сделала вид, что недовольна этим, внутренне Она улыбалась и чувствовала глубокое удовлетворение. Внешне же Она нахмурила брови и стала притворно отбиваться от Кришны. В этом состоянии Радхарани была необычайно красива, и Шрила Рупа Госвами прославляет Ее красоту. Это пример проявления различных переживаний в экстатической любви, вызванных одной причиной — Кришной.")]),v._v(" "),p("p",[v._v("Иногда в доме Махараджи Нанды устраивались большие празднества, на которые собирались все жители Вриндавана. Во время одного из таких праздников на Шримати Радхарани было золотое ожерелье, подаренное Ей Кришной. Это сразу заметили Яшода и мать Радхарани — ожерелье было слишком велико для Ее шеи. При этом Радхарани видела и находившегося неподалеку Кришну, и Своего мужа, Абхиманью. Радхарани, оказавшуюся в такой ситуации, охватило сильное чувство стыда, и лицо Ее исказилось, став от этого еще прекраснее. В данном случае переплелись смущение, гнев, ликование и сожаление. Это пример объединения нескольких признаков экстатической любви.")]),v._v(" "),p("p",[v._v("Однажды Камса воскликнул: «Что плохого может сделать мне этот мальчишка? Это не в Его силах!» Но в следующее мгновение Камсе сообщили, что все его друзья пали от руки этого мальчика. Услышав об этом, Камса растерялся: «Может быть, мне следует, не откладывая, пойти и сдаться на Его милость? Но пристало ли это великому воину?» Тут же ему пришла в голову другая мысль: «Чего ради я должен бояться Его? Ведь у меня по-прежнему много борцов, готовых встать на мою защиту». Однако сразу после этого он подумал: «Нет сомнений в том, что это не обыкновенный мальчик. Левой рукой Он поднял холм Говардхана. Что же мне теперь делать? Может быть, пойти во Вриндаван и учинить расправу над его жителями? Но я не в силах даже выйти из дворца, потому что сердце мое сжимается от страха перед этим мальчишкой!» Состояние Камсы — пример одновременного проявления гордости, сожаления, робости, решимости, памятования, неуверенности, гнева и страха. Таким образом, состояние ума Камсы определялось сочетанием восьми различных чувств. Это другой пример объединения признаков экстатической любви в отчаянии.")]),v._v(" "),p("p",[v._v("Один преданный-семьянин как-то воскликнул: «Господи, я такой презренный человек, что мои глаза — да будут они прокляты! — даже не хотят увидеть славный город Матхуру. Я получил хорошее образование, но использовал его только на службе царю. Я забыл, что ничто не может устоять перед беспощадным временем, которое все создает и все разрушает. Кому мне завещать все свои богатства? Я старею. Что мне делать? Заниматься преданным служением дома? Для меня это не подходит, потому что мой ум влечет к себе трансцендентная земля Вриндавана».")]),v._v(" "),p("p",[v._v("Это пример сочетания отчаяния, гордости, сомнений, терпения, сожалений, решимости и сильного желания — семи различных признаков экстатической любви к Кришне.")]),v._v(" "),p("p",[v._v("Санскритская пословица гласит: «Самое большое удовлетворение рождается из отчаяния». Иначе говоря, когда амбиции или желания человека становятся непомерными и их осуществление приходит только после того, как он уже потерял всякую надежду, достижение цели приносит самое глубокое удовлетворение. Однажды мальчики-пастушки во Вриндаване впустую проискали Кришну целый день. От этого лица их почернели, а тела поблекли. Но стоило им услышать звуки флейты Кришны, доносившиеся с холма, как их охватила необычайная радость. Это пример удовлетворения, которое испытывает отчаявшийся.")]),v._v(" "),p("p",[v._v("Шрила Рупа Госвами говорит, что, хотя он и не сведущ во всех тонкостях, оттенках и расах признаков экстатической любви, он все же попытался привести несколько примеров различных проявлений любви к Кришне. Далее он утверждает, что тридцать три выводящих из равновесия проявления экстатической любви плюс восемь других признаков составляют сорок один основной признак экстатической любви. Экстатические эмоции изменяют жизнедеятельность тела и сказываются на том, как действуют органы чувств человека. Их можно считать различными переживаниями сердца. Некоторые из этих переживаний могут быть органичными для конкретного преданного, а некоторые лишь ненадолго охватывают его. Органичные для преданного признаки экстаза проявляются у него постоянно — как внутренне, так и внешне.")]),v._v(" "),p("p",[v._v("Одного взгляда на ткань бывает достаточно, чтобы определить, какой краской она окрашена. Точно так же, если нам известны значения многообразных проявлений экстатической любви, о которых здесь идет речь, этого одного будет достаточно, чтобы определить состояние человека. Иначе говоря, хотя привязанность к Кришне едина, у разных преданных она проявляется по-разному. Как ткань, окрашенная в красный цвет, выглядт красной, так и о переживаниях, которые в данный момент испытывает преданный, можно судить по характерным для них признакам экстаза. Фактически все разнообразные расы и состояния, в которых находятся преданные, порождают в их уме уникальные ощущения и переживания, которые, в свою очередь, определяют различия в формах и силе проявления признаков экстатической любви. Человек может быть очень благородным, уравновешенным и щедрым или же грубым и черствым — экстатическая любовь в этих случаях будет проявляться по-разному, в зависимости от состояния его сердца. Как правило, один человек неспособен глубоко понять склад ума другого, но, если у кого-то доброе и мягкое сердце, признаки экстатической любви будут проявляться у него очень ярко и их нетрудно будет заметить. Сердце возвышенного и уравновешенного человека можно сравнить с золотом, а мягкого и доброго — с клочком ваты. От экстатических переживаний в уме золотое, или уравновешенное, сердце не возбуждается, тогда как мягкое сразу приходит в волнение.")]),v._v(" "),p("p",[v._v("Это можно пояснить на следующем примере. Уравновешенное, щедрое сердце сравнивают с большим городом, а мягкое — с маленькой хижиной. В большом городе много огней и даже огромных слонов, но никто не обращает на них особого внимания. Однако рядом с маленькой хижиной такие огни или слоны каждому бросятся в глаза.")]),v._v(" "),p("p",[v._v("Жесткое сердце сравнивают с молнией, золотом и смолой. Молния обладает огромной силой, и ее невозможно смягчить. Подобно этому, сердца тех, кто подвергает себя суровым аскезам и покаяниям, смягчить очень трудно. Золотое сердце плавится при высокой температуре, например в экстатической любви, а сердце из смолы тает при небольшом нагреве.")]),v._v(" "),p("p",[v._v("Мягкое сердце сравнивают с медом, сливочным маслом и нектаром, а состояние ума — с солнечным светом. Для того чтобы растопить масло и мед, достаточно дать им постоять на солнце. Так же легко тают и мягкосердечные люди. А нектар по природе всегда жидкий. Так и сердца тех, кто исполнен чистой экстатической любви к Кришне, всегда пребывают в жидком состоянии, подобно нектару.")])])}),[],!1,null,null,null);_.default=e.exports}}]);