(window.webpackJsonp=window.webpackJsonp||[]).push([[125],{497:function(v,_,p){"use strict";p.r(_);var t=p(45),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"глава-35-неитральная-любовь-к-богу"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#глава-35-неитральная-любовь-к-богу"}},[v._v("#")]),v._v(" Глава 35. Нейтральная любовь к Богу")]),v._v(" "),p("p",[v._v("Шрила Рупа Госвами возносит почтительные молитвы вечной Верховной Личности Бога, тому, кто неизменно прекрасен и кому всегда с трансцендентной любовью служат чистые преданные. Третья часть «Бхакти-расамрита-синдху» посвящена описанию пяти первичных видов преданного служения: служению в нейтральных отношениях, в отношениях слуги и господина, в дружеских отношениях, в родительской и супружеской любви. Эти пять форм отношений с Господом, которые будут подробно рассмотрены здесь, образно называют пятью волнами западной части океана преданности.")]),v._v(" "),p("p",[v._v("О том, кто действительно способен постоянно находиться на трансцендентном уровне, говорят, что он достиг нейтральной стадии преданного служения. Некоторые великие мудрецы достигали нейтральной стадии, обуздав свои чувства с помощью аскезы, покаяния и медитации. Таких мудрецов обычно называют йогами-мистиками, и в большинстве случаев их привлекает духовное блаженство, которое приносит осознание безличного аспекта Абсолютной Истины. Им практически незнакомо трансцендентное наслаждение личного общения с Верховным Господом.")]),v._v(" "),p("p",[v._v("На самом деле трансцендентное наслаждение общения с Верховной Личностью намного превосходит блаженство осознания безличного Брахмана, ибо при этом человек непосредственно встречается с Господом в Его вечном образе. Слушание повествований об играх Господа само по себе не приносит имперсоналистам трансцендентного блаженства общения с Ним, поэтому темы, обсуждаемые в «Бхагавад-гите», где Сам Господь беседует с Арджуной, не доставляют имперсоналистам трансцендентного наслаждения. Их отношение к Господу основано на безличностной концепции, что лишает их возможности познать трансцендентное наслаждение, доступное преданным, которые считают Бога Верховной Личностью. Поэтому комментарии имперсоналистов к «Бхагавад-гите» вредны: не поняв трансцендентного блаженства, заключенного в «Гиге», они хотят трактовать ее по-своему. Однако если имперсоналист общается с чистым преданным, то получает возможность занять более высокое трансцендентное положение. Поэтому, чтобы великие мудрецы могли познать высочайшее трансцендентное наслаждение, им рекомендуют поклоняться образу Господа.")]),v._v(" "),p("p",[v._v("Не поклоняясь арча-виграхе, то есть мурти (образу) Господа, невозможно понять такие писания, как «Бхагавад-гита» и «Шримад-Бхагаватам». Для начала мудрецам, занимающим позицию трансцендентного нейтралитета, следует принять покровительство Господа Вишну, четырехрукой вечной формы Верховной Личности Бога.")]),v._v(" "),p("p",[v._v("Йогам-мистикам рекомендуют медитировать на образ Господа Вишну, как учит Капила Муни, основатель санкхья-йоги. К сожалению, многие йоги-мистики пытаются медитировать на пустоту, что, по словам «Гиты», очень трудно и не может принести никаких ощутимых результатов.")]),v._v(" "),p("p",[v._v("Совершив множество аскетических подвигов и получив возможность лицезреть четырехрукий трансцендентный образ Вишну, великие святые сказали: «Этот синий четырехрукий образ Господа — источник всей радости и средоточие нашей жизненной силы. Когда мы видим этот вечный образ Вишну, нас, как и многих других парамахамс, мгновенно пленяет красота Господа». Такое отношение к Господу Вишну — пример шанта-расы, нейтральной стадии преданного служения. Стремящиеся к освобождению пытаются вырваться из материального рабства с помощью аскезы и умерщвления плоти. В конечном счете они постигают безличный аспект Трансцендентного. В соответствии с «Бхагавад-гитой», признаки освобождения от материальных пут на стадии брахма-бхуты таковы: человек исполняется радости, ничего не желает, ни о чем не скорбит и обретает понимание единой основы всего сущего. На уровне шанта-расы, нейтральной стадии преданного служения, преданный преклоняется перед Господом в образе Вишну.")]),v._v(" "),p("p",[v._v("Фактически вся ведическая культура направлена на постижение Господа Вишну. В одной из мантр «Риг-веды» говорится, что совершенные святые всегда стремятся медитировать на лотосные стопы Вишну.")]),v._v(" "),p("p",[v._v("Как сказано в «Шримад-Бхагаватам», только глупцы не ведают, что высшей целью жизни является Вишну. Все авторитетные ведические писания сходятся на том, что, когда человек осознает величие Вишну, это знаменует начало его преданного служения. И если он будет продолжать под руководством духовного учителя совершенствоваться в преданном служении, то постепенно ему откроются и другие аспекты преданного служения. На стадии шанта-расы человек обретает способность созерцать Господа Вишну, Верховную Личность Бога, дарующего освобождение даже демонам. Такой кандидат в преданные почитает Господа как вечный трансцендентный образ, как предводителя всех осознавших себя душ, как Сверхдушу и Верховный Брахман. Он также преклоняется перед Ним за Его невозмутимость, абсолютное владение Собой и чистоту, за Его доброту к преданным и за то, что Он не подвержен влиянию материальных обстоятельств. Такое благоговейное и почтительное отношение к Господу Вишну — признак того, что святой человек находится в шанта-расе, на нейтральной стадии преданного служения.")]),v._v(" "),p("p",[v._v("Имперсоналист может достичь уровня шанта-расы, только общаясь с чистыми преданными, и никак иначе. Начав общаться с чистым преданным, смиренно внимая наставлениям Господа Кришны и не пытаясь истолковать их по-своему, освобожденная душа, осознавшая Брахман, может достичь нейтральной стадии преданного служения. Лучшим примером святых личностей, находящихся в шанта-расе, являются братья Кумары: Санака, Санатана, Сананда и Санат. Эти четверо святых (иногда их называют Чатухсаной) — сыновья Господа Брахмы. Родившись, они отказались выполнять повеление отца обзавестись семьями и увеличивать численность людей. Они сказали, что твердо решили не связывать себя узами семейной жизни; вместо этого они хотели посвятить себя самосовершенствованию и вести жизнь хранящих целомудрие брахмачари. Вот почему, прожив миллионы лет, эти великие святые по-прежнему выглядят четырех-пятилетними мальчиками. У них светлая кожа, их тела излучают сияние, и они путешествуют повсюду нагими. Эти четверо святых почти никогда не разлучаются друг с другом.")]),v._v(" "),p("p",[v._v("В одной из молитв братьев Кумаров говорится: «О Господь Мукунда [Кришна, дарующий освобождение], безличный аспект Абсолютной Истины, называемый Брахманом, может привлекать святого человека только до тех пор, пока ему не посчастливится увидеть Твой вечный образ, исполненный блаженства и знания, который темно-синим цветом напоминает молодое дерево тамала».")]),v._v(" "),p("p",[v._v("«Бхакти-расамрита-синдху» так описывает качества святого человека: святой — это тот, кто глубоко сознает, что достаточно заниматься одним преданным служением, чтобы быть уверенным в своем освобождении. Он всегда следует предписанным правилам жизни в преданном служении, стремясь при этом освободиться от пут материи.")]),v._v(" "),p("p",[v._v("Как правило, святой думает: «Когда я смогу жить в полном одиночестве в какой-то горной пещере? Когда я смогу обходиться одной набедренной повязкой, небольшим количеством фруктов и овощей? Когда, наконец, я смогу постоянно думать о лотосных стопах Мукунды — источника сияния Брахмана? Достигну ли я когда-нибудь такого уровня духовного развития, на котором полностью осознаю всю мимолетность моих дней и ночей в сравнении с вечностью?»")]),v._v(" "),p("p",[v._v("Преданные, осознавшие себя души, проповедующие славу Господа, всегда хранят в своем сердце экстатическую любовь к Господу и потому купаются в лучах луны экстаза. Таких людей называют святыми.")]),v._v(" "),p("p",[v._v("Святым человеком движет желание изучать ведические писания, особенно Упанишады, всегда жить вдали от мирской суеты, постоянно размышлять о вечном образе Кришны, быть готовым обсуждать все, что связано с Абсолютной Истиной, всегда блистать своими знаниями, видеть Верховного Господа в Его вселенской форме (вишва-рупе), постоянно общаться с преданными, сведущими в духовной науке, и обсуждать с такими же возвышенными, как он сам, людьми смысл Вед. Все эти качества святого человека помогают ему достичь уровня шанта-расы.")]),v._v(" "),p("p",[v._v("В «Бхакти-расамрита-синдху» говорится, что все присутствовавшие там, где Господь Брахма учил Ведам, в частности Упанишадам, исполнились экстатической любви к Кришне, предводителю рода Яду. По сути дела, цель изучения Упанишад — постичь Верховную Личность Бога. Отрицание материального существования является лишь одной из тем, рассматриваемых в Упанишадах. Следующим шагом должно стать осознание безличного аспекта Абсолютной Истины. Только после этого, миновав уровень безличного Брахмана и поднявшись до уровня общения с Верховной Личностью Бога, человек достигает высшей цели изучения Упанишад.")]),v._v(" "),p("p",[v._v("Для тех, кто находится на уровне шанта-расы, стимулом к совершенствованию в преданном служении могут быть аромат листьев туласи, предложенных лотосным стопам Господа, звуки Его раковины, вид святого места где-то в горах или созерцание леса, к примеру одного из лесов Вриндавана, паломничество по святым местам, посещение мест, по которым протекает Ганга, победа над потребностями тела (едой, сном, совокуплением и самозащитой), осознание разрушительной силы вечного времени и постоянное общение с теми, кто занят преданным служением в сознании Кришны. Все это помогает возвышенным людям, находящимся на уровне шанта-расы, достичь более высокого уровня в преданном служении.")]),v._v(" "),p("p",[v._v("В Третьей песни «Шримад-Бхагаватам» (15.43) говорится о четырех святых (Чатухсане), во главе с Санака-Кумаром. Они прибыли на Вайкунтхалоку, расположенную в духовном небе, чтобы увидеть Господа. Когда они припали к Его стопам, их ноздрей достиг аромат листьев туласи и шафрана, мгновенно завладевший их сознанием. Хотя эти четверо святых всегда были поглощены размышлениями о безличном Брахмане, близость Господа и аромат листьев туласи заставили волосы на их теле подняться дыбом. Таким образом, даже тот, кто осознал Брахман, начав общаться с преданными и занявшись преданным служением, может сразу ощутить привлекательность личностного аспекта Господа.")]),v._v(" "),p("p",[v._v("Великих мудрецов, достигших в преданном служении уровня шанта-расы, отличают следующие признаки. Их взгляд сосредоточен на кончике носа, и ведут они себя как авадхуты. Авадхутами называют великих мистиков, игнорирующих социальные, религиозные и ведические условности. Другими признаками таких людей является то, что они не спешат выступать с речами перед публикой, а говоря что-либо, соединяют большой и указательный пальцы (это называется гьяна-мудрой). Они ничего не имеют против атеистов и не выказывают особого расположения к преданным. Основное внимание они уделяют достижению освобождения и безразличия к материалистическому образу жизни. Они всегда бесстрастны, ни к чему не привязаны и не отождествляют себя ни с чем материальным. Они неизменно серьезны и полностью поглощены размышлениями о Верховной Личности Бога. Все эти редкие качества развиваются у преданных, находящихся в шанта-расе.")]),v._v(" "),p("p",[v._v("Что касается сосредоточения взгляда на кончике носа, то можно привести цитируемое в «Бхакти-расамрита-синдху» высказывание одного преданного, который заметил, как этим занимается некий йог. Он сказал: «Взгляд этого великого мудреца сосредоточен на кончике носа. Похоже, что он уже познал внутри себя вечный образ Господа».")]),v._v(" "),p("p",[v._v("Иногда можно видеть, как преданный, находящийся в шанта-расе, зевает, потягивается, наставляет других в преданном служении, в почтении склоняется перед образом Господа, возносит искренние молитвы и хочет физически служить Господу. Таковы некоторые общие признаки преданных в нейтральной расе. Заметив, как один из преданных зевает, другой обратился к нему с такими словами: «О мистик, я полагаю, что в твоем сердце до некоторой степени развились экстатическая любовь и преданность, заставляющие тебя зевать». Иногда преданный в шанта-расе падает наземь, волосы на его теле встают дыбом и по всему телу пробегает дрожь — так у преданного непроизвольно проявляются различные признаки экстатического транса.")]),v._v(" "),p("p",[v._v("В «Бхакти-расамрита-синдху» говорится, что, когда Господь Кришна подул в Свою раковину Панчаджанью, многие великие мудрецы, жившие в горных пещерах, очнулись, выйдя из медитативного транса, и заметили, что волосы у них на теле стоят дыбом. Иногда преданные, находящиеся в шанта-расе, цепенеют, а иногда их охватывает ликование, беспокойство либо стремление философствовать или спорить; они могут быть невозмутимыми, осмотрительными, задумчивыми и умелыми. Эти признаки свидетельствуют о постоянном экстазе, то есть об устойчивых чувствах.")]),v._v(" "),p("p",[v._v("Один великий мудрец, осознавшая себя душа, сокрушался, что упустил случай увидеть Верховного Господа, Кришну, когда тот жил в Двараке. Эта мысль заставила мудреца замереть в оцепенении. Он понял, что жизнь его прошла напрасно. Иначе говоря, он сожалел, что из-за своей медитации не воспользовался возможностью увидеться с Самим Богом, Верховной Личностью.")]),v._v(" "),p("p",[v._v("О мистике, поднявшемся над умозрительным философствованием и утвердившемся в Брахмане, говорят, что он находится в трансе и свободен от влияния материальных представлений о жизни. Когда такой мистик слушает повествования об играх Господа, по его телу порой пробегает дрожь.")]),v._v(" "),p("p",[v._v("Когда преданный, осознавший Брахман и достигший уровня устойчивого транса, непосредственно созерцает вечный образ Кришны, его трансцендентное блаженство возрастает в миллионы раз. Один великий мудрец спросил другого: «Друг мой, как ты думаешь, если я достигну совершенства в восьмиступенчатой йоге, смогу ли я увидеть вечный образ Верховной Личности Бога?» Этот вопрос — пример проявления любознательности, присущей преданным на нейтральной стадии преданного служения.")]),v._v(" "),p("p",[v._v("Когда по случаю солнечного затмения на Курукшетру приехали на колеснице Господь Кришна, Его старший брат Баларама и сестра Субхадра, туда же прибыли и многие йоги-мистики. Увидев Господа Кришну и Балараму, йоги-мистики воскликнули, что изумительное сияние, исходящее от тела Господа, заставило их почти забыть о наслаждении, которое они черпали в медитации на безличный Брахман. Один из этих мистиков, приблизившись к Кришне, сказал: «О Господь, Ты всегда исполнен трансцендентного блаженства, недоступного ни на каких иных уровнях духовного осознания, поэтому мне было достаточно издали увидеть Тебя, чтобы понять бессмысленность моего пребывания в трансцендентном блаженстве безличного Брахмана».")]),v._v(" "),p("p",[v._v("Когда звуки Панчаджаньи, раковины Кришны, вывели из медитативного транса некоего великого мистика, он был так потрясен, что стал биться головой о землю. Глаза его наполнились слезами экстатической любви, и он отступил от всех правил и предписаний своей йоги. Так, ни минуты не колеблясь, он отказался от практики постижения безличного Брахмана.")]),v._v(" "),p("p",[v._v("В «Кришна-карнамрите» Билвамангала Тхакур говорит: «Пусть имперсоналисты занимаются познанием духа, поклоняясь безличному Брахману. Когда-то я тоже шел этим путем, но меня увел с него юный проказник, лукавый и очень привязанный к гопи. Он сделал меня Своей служанкой, так что теперь я забыл, что значит медитировать на Брахман».")]),v._v(" "),p("p",[v._v("Билвамангала Тхакур получил духовное посвящение в школе имперсоналистов и стремился познать безличный аспект Абсолютной Истины, но позже, повстречав во Вриндаване Кришну, стал возвышенным преданным. То же случилось с Шукадевой Госвами, который по милости Господа обратился к преданному служению, оставив путь осознания безличного Абсолюта.")]),v._v(" "),p("p",[v._v("Шукадева Госвами и Билвамангала Тхакур, отказавшиеся от безличных представлений об Абсолютной Истине и посвятившие себя преданному служению, — лучшие примеры преданных в шанта-расе.  Здесь можно привести наставление, которое дал Уддхаве Сам Господь Кришна в Одиннадцатой песни «Шримад-Бхагаватам»: «О том, кто сосредоточен на Моем личностном образе, говорят, что он находится в шанта-расе, и, пока человек не достигнет этого уровня, не может быть и речи о том, чтобы он обрел чистое преданное служение Мне». Иначе говоря, не достигнув, по крайней мере, уровня шанта-расы, никто не может вступить в личное общение с Верховной Личностью Бога.")])])}),[],!1,null,null,null);_.default=e.exports}}]);