(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{396:function(v,_,p){"use strict";p.r(_);var t=p(45),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"глава-3-кто-может-заниматься-преданным-служением"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#глава-3-кто-может-заниматься-преданным-служением"}},[v._v("#")]),v._v(" Глава 3. КТО МОЖЕТ ЗАНИМАТЬСЯ ПРЕДАННЫМ СЛУЖЕНИЕМ")]),v._v(" "),p("p",[v._v("Общение с махатмами, великими душами, целиком поглощенными преданным служением Господу, помогает человеку до некоторой степени ощутить привлекательность Шри Кришны. При этом такой человек может оставаться очень привязанным к кармической деятельности и материальному чувственному наслаждению и не быть готовым к разным формам отречения от мира. И все же, если его постоянно влечет к Кришне, он получает возможность заниматься преданным служением.")]),v._v(" "),p("p",[v._v("Привязанность к сознанию Кришны, которая возникает в процессе общения с чистыми преданными, — признак великой удачи. Господь Чайтанья подтверждает, что только удачливые люди по милости истинного духовного учителя и Кришны получают семя преданного служения. В связи с этим можно привести слова Господа Кришны из Одиннадцатой песни «Шримад-Бхагаватам» (20.8): «Дорогой Уддхава, лишь у того, на чью долю выпала редкая удача, возникает тяга ко Мне. И даже если он не до конца избавился от привязанности к кармической деятельности и не очень сильно привязан к преданному служению, его служение быстро принесет плоды».")]),v._v(" "),p("p",[v._v("Преданных можно разделить на три категории. Преданному первой, высшей категории присущи следующие черты. Он очень хорошо изучил богооткровенные писания и может умело приводить основанные на них аргументы. Он способен, сообразуясь с обстоятельствами, блестяще представить выводы священных писаний и убедительно обосновать методы преданного служения. Он глубоко убежден, что высшей целью жизни является обретение трансцендентного любовного служения Кришне, и знает, что Кришна — единственный объект поклонения и любви. К преданным первой категории относятся те, кто прошел обучение у истинного духовного учителя, неукоснительно следуя всем правилам и ограничениям, и искренне повиновался ему в соответствии с указаниями богооткровенных писаний. Такие люди обладают всеми качествами, необходимыми для того, чтобы проповедовать и самим стать духовными учителями, поэтому их считают преданными высшей категории. Они никогда не нарушают принципов, которые даны свыше, и непоколебимо верят в священные писания, так как достигли стройного, логичного понимания того, что в них сказано. Говоря о логике, о доказательствах, мы имеем в виду логику и доказательства, основанные на богооткровенных писаниях. Преданный высшей категории равнодушен к умозрительным рассуждениям, которые представляют собой бессмысленную трату времени. Иначе говоря, преданным высшей категории можно считать того, кто идет путем преданного служения, обладая зрелой решимостью.")]),v._v(" "),p("p",[v._v("Преданного второй категории отличают следующие признаки. Он не всегда может убедительно отстоять свою позицию, подтвердив ее свидетельствами из богооткровенных писаний, но непоколебимо верит в свою цель. Иначе говоря, преданный второй категории твердо верит в путь преданного служения Кришне, однако иногда в споре с оппонентами может потерпеть поражение, не сумев привести необходимые аргументы и подкрепить свою позицию выводами богооткровенных писаний. Но даже это не способно поколебать его внутреннюю уверенность в правильности своего выбора Кришны как высшего объекта поклонения.")]),v._v(" "),p("p",[v._v("Неофит, то есть преданный третьей категории, — это тот, кто еще недостаточно тверд в своей вере и в то же время не знает выводов богооткровенных писаний. Вера неофита может быть поколеблена чьими-то сильными аргументами или возражениями. В отличие от преданного второй категории, который тоже не способен приводить аргументы и доказательства из богооткровенных писаний, но тем не менее твердо верит в свою цель, неофит не обладает такой верой. Поэтому его называют начинающим преданным.")]),v._v(" "),p("p",[v._v("Классификация преданных-неофитов содержится в «Бхагавад-гите». Там говорится о четырех типах людей, приступающих к преданному служению: попавших в беду, нуждающихся в деньгах, любознательных и мудрых. Такие люди обращаются к Богу, движимые желанием обрести то, чего им недостает. Они приходят в места, где поклоняются Богу, и молят Его облегчить их материальные страдания, улучшить экономическое положение или удовлетворить их любопытство. Мудрого человека, который осознал только величие Бога, также причисляют к неофитам. Неофиты могут достичь уровня преданных второй или даже первой категории, если будут общаться с чистыми преданными.")]),v._v(" "),p("p",[v._v("Примером неофита является Махараджа Дхрува. Он занялся преданным служением Господу, чтобы получить царство своего отца, но в конце концов, полностью очистившись, отказался принять от Господа материальное вознаграждение. Подобно этому, Гаджендра, попав в беду, взмолился Господу Кришне, прося Его о защите, и так стал чистым преданным. Санака-, Санатана-, Сананда- и Санат-Кумары относились к категории мудрых, святых людей, и их также привлекло преданное служение. То же самое произошло с мудрецами из леса Наймишаранья во главе с Шаунакой Муни. Движимые любопытством, они задали Суте Госвами много вопросов о Кришне. Так, получив возможность общаться с чистым преданным, они сами стали чистыми преданными. Таков путь, позволяющий подняться на более высокий духовный уровень: в каких бы условиях ни находился человек, если ему выпадает счастливая возможность общаться с чистыми преданными, он очень быстро становится преданным второй или первой категории.")]),v._v(" "),p("p",[v._v("Эти четыре вида преданных описаны в седьмой главе «Бхагавад-гиты», и всех их считают людьми праведными. Пока человек не станет праведником, он не сможет прийти к преданному служению. В «Бхагавад-гите» говорится, что принять сознание Кришны может только тот, кто в течение долгого времени совершал праведные поступки и на чью жизнь больше не оказывают влияния последствия его прошлых грехов. Никто другой не способен на это. Деление преданных-неофитов на четыре группы — попавших в беду, нуждающихся в деньгах, любознательных и мудрых — проводится в соответствии с тем, насколько велик у них запас благочестивой деятельности. Если человек не накопил благочестия, то, попав в тяжелое положение, он становится либо агностиком, либо коммунистом, либо кем-нибудь еще в том же роде. Не обладая твердой верой в Бога, такой человек полагает, что сможет справиться со своими бедами, если полностью отвергнет Его существование.")]),v._v(" "),p("p",[v._v("Далее в «Гите» Господь Кришна объяснил, что из четырех видов неофитов Ему дороже всего мудрые, так как мудрый человек, если он привязан к Кришне, не желает взамен ничего материального. Мудрый человек, у которого возникла привязанность к Кришне, не ждет от Него в ответ ничего: ни облегчения своих страданий, ни денег. Это значит, что с самого начала его привязанность к Кришне в той или иной мере основывается на любви. Более того, благодаря своей мудрости и изучению шастр (священных писаний) он понимает, что Кришна является Верховной Личностью Бога.")]),v._v(" "),p("p",[v._v("«Бхагавад-гита» подтверждает, что после многих и многих рождений человек, обретший истинную мудрость, предается Васудеве, в совершенстве осознав, что Кришна (Васудева) — источник всего сущего и причина всех причин. Поняв это, он припадает к лотосным стопам Кришны и постепенно проникается любовью к Нему. Хотя такой мудрый человек особенно дорог Кришне, других преданных-неофитов также считают великими душами, потому что, охваченные горем или нуждаясь в деньгах, они обратились за помощью к Кришне. Именно поэтому их тоже называют махатмами, свободно, широко мыслящими людьми.")]),v._v(" "),p("p",[v._v("Пока человек не достигнет уровня гьяни, мудреца, он не сможет оставаться верным принципу поклонения Верховной Личности Бога. Менее разумные люди, то есть те, кто, поддавшись чарам майи, утратил разум, под влиянием гун природы привязываются к полубогам. Мудрец — это тот, кто очень хорошо понял, что он не тело, а вечная душа. Поскольку такой человек сознает, что по природе своей он духовен и что Кришна — Высший Дух, ему понятно, что он должен быть тесно связан с Кришной, а не со своим телом. Страждущие и нуждающиеся в деньгах люди имеют материальные представления о жизни: ведь и несчастье, и нужда относятся к материальному телу. Любознательный человек находится на немного более высокой ступени развития, чем попавшие в беду или нуждающиеся в деньгах, однако и он пребывает еще на материальном уровне. Мудрый же человек, ищущий Кришну, прекрасно понимает, что сам он вечная душа, Брахман, а Кришна — это высшая из всех душ, Парабрахман. Он знает, что, будучи подчиненной и ограниченной, обыкновенная душа всегда должна поддерживать связь с безграничной Высшей Душой — Кришной. Таковы взаимоотношения мудрого человека с Кришной.")]),v._v(" "),p("p",[v._v("Таким образом, чистым преданным служением может начать заниматься тот, кто избавился от телесных представлений о жизни. В «Бхагавад-гите» подтверждается, что доступ к чистому преданному служению открывается человеку только тогда, когда он, осознав Брахман, избавляется от всех материальных тревог и обретает способность видеть единую природу всех живых существ.")]),v._v(" "),p("p",[v._v("Как говорилось выше, счастье бывает трех видов: материальное, духовное и счастье преданного служения. Преданное служение и счастье, которое оно приносит, недостижимы для человека, находящегося под влиянием материи. Стремление к материальному наслаждению, также, как и стремление к слиянию со Всевышним, продиктовано материальными представлениями о жизни. Имперсоналисты ставят своей конечной целью слияние с Господом, поскольку не способны по достоинству оценить духовное счастье общения с Верховной Личностью Бога, любовных взаимоотношений с Господом. Их концепция является всего лишь логическим продолжением материальных представлений. В материальном мире каждый пытается возвыситься над всеми, кто его окружает. Человек с материальными представлениями о жизни всегда стремится занять главенствующее положение в своей общине, сословии или стране. Стремление стать выше других может безгранично возрастать и превратиться в желание слиться воедино с величайшим из всех — Верховным Господом. Это тоже материальная идея, хотя, возможно, и несколько более возвышенная.")]),v._v(" "),p("p",[v._v("Совершенная духовная концепция жизни подразумевает полное знание о своем изначальном положении — знание, благодаря которому душа посвящает себя трансцендентному любовному служению Господу. Человек должен понять, что он конечен, а Господь бесконечен, поэтому на самом деле стать единым с Господом невозможно, как бы мы того ни желали. Это просто невозможно. Любой, у кого сохранилось хоть малейшее желание или склонность удовлетворять свои чувства, занимая все более и более высокое положение (в материальном или духовном смысле), лишается возможности изведать истинную сладость преданного служения. Поэтому Шрила Рупа Госвами говорит, что желать бхукти (материальных наслаждений) или мукти (освобождения) — все равно что находиться под властью магических чар какой-нибудь колдуньи: и в том и в другом случае положение человека незавидно. Бхукти означает материальное наслаждение, а мукти —освобождение от материальных тревог и слияние с Богом. Иметь эти желания — все равно что быть одержимым духами, потому что до тех пор, пока у человека остается стремление к материальному наслаждению или духовному слиянию со Всевышним, он не в состоянии ощутить подлинный трансцендентный вкус преданного служения.")]),v._v(" "),p("p",[v._v("Чистый преданный никогда не стремится к освобождению. Господь Чайтанья Махапрабху обращался к Кришне с такой молитвой: «О сын Нанды, мне не нужно материального счастья нив каком виде: я не хочу ни многочисленных последователей, ни огромных богатств, ни прекрасной жены. Мне не нужно даже избавления от материального существования. Я согласен рождаться вновь и вновь и молю Тебя лишь о том, чтобы моя преданность Тебе всегда оставалась непоколебимой».")]),v._v(" "),p("p",[v._v("Внимание чистого преданного настолько сосредоточено на прославлении игр, имени, качеств, форм Господа и всего, связанного с Ним, что у него пропадает всякий интерес к мукти. Шри Билвамангала Тхакур говорит: «Когда я занят преданным служением Тебе, о мой Господь, мне совсем нетрудно ощущать Твое присутствие повсюду. Что же касается освобождения, я думаю, оно стоит у моих дверей с молитвенно сложенными руками и ожидает моих приказаний». Таким образом, чистые преданные не придают особого значения духовному освобождению, спасению.")]),v._v(" "),p("p",[v._v("В связи с этим в Третьей песни «Шримад-Бхагаватам» (25.36) Капиладева говорит Своей матери, Девахути: «Дорогая мать, Мои чистые преданные пленены Моими обликами, красотой Моего лица и тела. Мой смех, Мои игры и взгляды обладают для них такой притягательной силой, что их умы всегда поглощены мыслями обо Мне, а их жизни целиком посвящены Мне. И хотя сами они не желают какого бы то ни было освобождения или материального счастья, Я все же делаю их Своими спутниками в Моей высшей обители».")]),v._v(" "),p("p",[v._v("Этот стих из «Шримад-Бхагаватам» обещает чистому преданному непосредственное общение с Верховной Личностью Бога, В связи С этим Шрила Рупа Госвами отмечает, что тому, кого действительно привлекает красота лотосных стоп Шри Кришны и служение Ему и чье сердце эта привязанность к Господу всегда наполняет трансцендентным блаженством, никогда не приходит в голову стремиться к освобождению, столь ценимому им персоналистам и.")]),v._v(" "),p("p",[v._v("Похожее высказывание есть в другом месте Третьей песни «Шримад-Бхагаватам» (4.15). Уддхава обращается к Господу Кришне с такими словами: «О мой Господь, ни религиозность, ни материальное процветание, ни чувственные наслаждения, ни освобождение Ничего не могут дать людям, занятым трансцендентным любовным служением Тебе, хотя счастье, которое приносит все это, легко доступно им. Мне нетрудно было бы получить любой из этих даров, но, несмотря на такую возможность, о мой Господь, я не стремлюсь к ним. Единственное, о чем я молю Тебя, — это даровать мне непоколебимую веру в Тебя и преданность Твоим лотосным стопам».")]),v._v(" "),p("p",[v._v("А вот еще одно близкое по смыслу высказывание из Третьей песни (25.34). Капиладева говорит, обращаясь к Своей матери: «Дорогая мать, преданные, чьи сердца всегда переполняет желание служить Моим лотосным стопам, те, кто готов все сделать ради Моего удовлетворения, особенно же самые удачливые из них, которые собираются вместе, чтобы познавать Мои качества, игры и облик, и вместе прославляют Меня, получая от этого трансцендентное блаженство, никогда не помышляют о слиянии со Мной. Что говорить о слиянии со Мной — они откажутся даже от положения, равного Моему в Моей обители, от богатств, которыми обладаю Я, и даже от личного общения со Мной в теле, подобном Моему, ибо преданное служение Мне само по себе приносит им полное удовлетворение».")]),v._v(" "),p("p",[v._v("В Четвертой песни «Шримад-Бхагаватам» (9.10) царь Дхрува говорит: «О мой Господь, трансцендентное наслаждение, обретаемое имперсоналистами на пути самоосознания, не идет ни в какое сравнение с трансцендентным блаженством, которое испытывают чистые преданные, медитируя на Твои лотосные стопы. Как же тогда понять Тебя людям, которые привязаны к плодам своего труда и в лучшем случае хотят подняться на более высокие, райские планеты? Как можно сравнивать их счастье со счастьем преданных?»")])])}),[],!1,null,null,null);_.default=e.exports}}]);