(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{395:function(v,_,p){"use strict";p.r(_);var t=p(45),e=Object(t.a)({},(function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"глава-4-превосходство-преданного-служения-над-любыми-формамиосвобождения"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#глава-4-превосходство-преданного-служения-над-любыми-формамиосвобождения"}},[v._v("#")]),v._v(" Глава 4. ПРЕВОСХОДСТВО ПРЕДАННОГО СЛУЖЕНИЯ НАД ЛЮБЫМИ ФОРМАМИОСВОБОЖДЕНИЯ")]),v._v(" "),p("p",[v._v("Насколько глубоко преданный привязан к служению Верховной Личности Бога, можно понять из высказывания Махараджи Притху (Ади-раджи), которое приводится в Четвертой песни «Шримад-Бхагаватам» (20.24). Он обращается к Верховной Личности Бога с такой молитвой: «О мой Господь, я никогда не попрошу Тебя об освобождении, о так называемом духовном спасении, если посленего Я лишусь меда трансцендентного блаженства — возможности слушать, как Тебя от всего сердца прославляют чистые преданные, воспевающие Твои лотосные стопы .Я всегда буду молить Тебя, о Господь, дать мне миллионы языков и миллионы ушей, чтобы я мог постоянно петь Тебе хвалу и слушать, как это делают другие».")]),v._v(" "),p("p",[v._v("Имперсоналисты хотят слиться с бытием Всевышнего, но, лишившись индивидуальности, вместе с ней утрачивают и возможность слушать прославления Верховного Господа и восхвалять Его. Поскольку им ничего неизвестно о трансцендентном образе Господа, они лишены возможности воспевать Его трансцендентные деяния и слушать о них. Иначе говоря, только поднявшись над освобождением, человек способен насладиться познанием трансцендентной славы Господа или постичь Его трансцендентный образ.")]),v._v(" "),p("p",[v._v("Аналогичное высказывание приводится в Пятой песни«Шримад-Бхагаватам» (14.44), где Шукадева Госвами обращается к Махарадже Парикшиту с такими словами: «Царь Бхарата, великая душа, был так сильно привязан к служению лотосным стопам Кришны, что ему не составило труда отказаться от власти над всей Землей, от привязанности к детям, друзьям и знакомым, царской роскоши и красавице-жене. Сама богиня процветания считала зачесть одаривать его всевозможными материальными благами — такая ему выпала удача, — но он не принимал их». Шукадева Госвами восхищается таким поведением царя Бхараты. Он говорит: «Тот, чье сердце привлекли трансцендентные качестваВерховной Личности Бога, Мадхусуданы, не стремится даже к освобождению, которого желают многие великие мудрецы, не говоря уже о материальных богатствах».")]),v._v(" "),p("p",[v._v("Похожие слова есть в Шестой песни «Бхагаватам» (11.25), где Вритрасура говорит Господу: «О мой Господь, оставив трансцендентное служение Тебе, я, может быть, и смогу достичь планеты, называемой Дхрувалокой [Полярной звезды], или обрести власть над всеми планетными системами вселенной. Однако я не стремлюсь к этому. Мне не нужны ни мистические совершенства, которые приносит практика йоги, ни духовное спасение. Единственное, чего я хочу, о Господь, — это общаться с Тобой и вечно служить Тебе».")]),v._v(" "),p("p",[v._v("Об этом говорит и Господь Шива в Шестой песни «Шримад-Бхагаватам» (17.28), обращаясь к Сати: «Дорогая Сати, те, кто предан Нараяне [Кришне], ничего не боятся. Достигают ли они высших планетных систем, избавляются ли от материального осквернения или попадают в адские условия жизни— где бы они ни оказались, у них не возникает страха. Благодаря тому, что они укрылись под сенью лотосных стоп Нараяны, они одинаково относятся к любому положению в материальном мире».")]),v._v(" "),p("p",[v._v("В Шестой песни «Шримад-Бхагаватам» (18.74) есть похожий стих, который произносит Индра, владыка райских планет. Он обращается к Дити с такими словами: «О мать, только люди, избавившиеся от всех желаний и целиком посвятившие себя преданному служению Господу, знают, что является для них истинным благом. Такие люди поистине действуют себе во благо, и никто лучше их не знает, как достичь совершенства в жизни».")]),v._v(" "),p("p",[v._v("В Седьмой песни «Шримад-Бхагаватам» (6.25) Махараджа Прахлада говорит: «Дорогие мои друзья, рожденные в безбожных семьях, самое редкое достижение в этом мире — суметь удовлетворить Верховную Личность Бога. Иначе говоря, если Верховный Господь Кришна будет доволен вами, то вы, несомненно, сможете осуществить любые желания, хранящиеся в глубине вашего сердца. А тогда какой смысл пытаться обрести плоды кармы, которые приходят к нам сами собой благодаря взаимодействию гун материальной природы? Какой смысл в духовном спасении, в освобождении от материального рабства? Если вы всегда поете хвалу Верховному Господу и наслаждаетесь нектаром Его лотосных стоп, во всем этом нет никакой необходимости». Из этих слов Махараджи Прахлады явствует, что тот, кому доставляет удовольствие прославлять Господа и слушать о Его трансцендентном величии, обрел богатство, с которым не могут сравниться никакие материальные блага, включая все плоды благочестивой ритуальной деятельности и жертвоприношений и даже освобождение от материального рабства.")]),v._v(" "),p("p",[v._v("Аналогичное утверждение есть в другом стихе Седьмой песни (8.42). Когда полубоги возносят молитвы Господу Нрисимхе, Индра, царь небес, говорит: «О верховный владыка, эти демоны покушались на нашу долю в жертвоприношениях, но одним Своим появлением Ты избавил нас от всех страхов. В сущности, мы имеем долю в жертвенных ритуалах только благодаря Тебе, ибо Ты верховный наслаждающийся всеми жертвоприношениями. Ты Сверхдуша всех живых существ, и потому на самом деле все принадлежит Тебе. Наши сердца долго переполнял ужас перед демоном Хираньякашипу. Но Ты столь милостив к нам, что ,убив его, избавил наши сердца от этого страха и дал нам возможность снова поместить в них Тебя. Для тех, кто занят трансцендентным любовным служением Тебе, все богатства, отобранные у нас демонами, не представляют никакой ценности. Преданные не желают даже освобождения, не говоря уже о материальных богатствах. В действительности все плоды жертвоприношений предназначены не для нашего наслаждения. Наш единственный долг — всегда служить Тебе, ибо все предназначено для Твоего наслаждения».")]),v._v(" "),p("p",[v._v("Смысл этих слов Индры в том, что ни одному живому существу, начиная с Брахмы и кончая ничтожным муравьем, не предназначено")]),v._v(" "),p("p",[v._v("наслаждаться материальными богатствами. От живых существ просто требуется предлагать все верховному владыке, Личности Бога. Поступая так, они естественным образом получают благо. Уместно снова привести пример с разными органами тела, участвующими в добывании нищи и приготовлении ее, чтобы в конечном счете она досталась желудку. После того как пища попадает в желудок, пользу от нее получают все части тела. Подобно этому, долг каждого —удовлетворять Верховного Господа, что само по себе принесет удовлетворение всем.")]),v._v(" "),p("p",[v._v("Похожий стих есть в Восьмой песни «Шримад-Бхагаватам»(3.20). Гаджендра говорит: «О мой Господь, мне не доводилось испытывать трансцендентное блаженство преданного служения Тебе, поэтому я обратился к Тебе с просьбой. Однако я знаю, что Твои чистые преданные, которые, служа лотосным стопам великих душ, освободились от всех материальных желаний, всегда погружены в океан трансцендентного блаженства и потому всегда находят удовольствие только в том, что воспевают Твои добродетели. Им незачем молить Тебя о чем-либо илистремиться к чему-либо еще».")]),v._v(" "),p("p",[v._v("В Девятой песни «Бхагаватам» (4.67) Господь Вайкунтхи так отвечает Дурвасе Муни: «Преданное служение доставляет Моим чистым бхактам такое удовлетворение, что они не стремятся даже к пяти видам освобождения: 1) слиянию со Мной, 2) обитанию на Моей планете, 3) владению Моими богатствами, 4) обретению облика, подобного Моему, и 5) непосредственному общению со Мной. Их не интересуют даже эти формы освобождения, по чему можно судить, как мало их заботит материальное богатство или избавление от материальных страданий».")]),v._v(" "),p("p",[v._v("А в Десятой песни «Шримад-Бхагаватам» (16.37) Нагапатни (жёны змея Калии) молят Господа: «О наш Господь, пыль с Твоих лотосных стоп обладает удивительными свойствами. Любой, кому выпадает удача коснуться этой пыли, избавляется от малейшего желания достичь райских планет, господствовать над всеми планетными системами, обрести мистические совершенства йоги и даже освободиться от материального существования. Иначе говоря, тот, кто поклоняется пыли с Твоих лотосных стоп, становится равнодушен ко всем другим достижениям».")]),v._v(" "),p("p",[v._v("В другом стихе Десятой песни (87.21) Шрути, олицетворенные Веды, так обращаются к Господу: «Дорогой Господь, постичь духов-")]),v._v(" "),p("p",[v._v("ную науку очень трудно. Ты пришел сюда в Своем истинном облике, специально чтобы объяснить нам этот труднейший предмет — науку о духе. И потому Твои преданные, ради общения с освобожденными ачарьями [учителями]оставившие домашний уют, теперь полностью поглощены служением Тебе, утратив всякий интерес к так называемому освобождению».")]),v._v(" "),p("p",[v._v("Поясняя этот стих, следует заметить, что духовное познание подразумевает понимание природы своего «я» и природы «сверх-я», то есть Сверхдуши. Индивидуальная душа и Сверх душа качественно одинаковы, поэтому их обеих называют Брахманом — духом. Но постичь науку о Брахмане очень нелегко. К этом устремятся многие философы, однако им не удается реально продвинуться по этом упути. В «Бхагавад-гите» также говорится, что среди многих миллионов людей с трудом можно найти одного, который пытается понять духовную науку, а из множества тех, кто пытается понять этот предмет, только один-два действительно постигли Верховную Личность Бога. В упомянутом стихе Десятой песни (87,21) говорится, что обрести духовное знание очень трудно. Поэтому, чтобы сделать его более доступным, Верховный Господь приходит в Своей изначальной форме, в облике Шри Кришны, и Сам наставляет одного из Своих близких преданных — Арджуну, давая таким образом возможность всем людям воспользоваться духовным знанием. Кроме того, в этом стихе объясняется, что освобождение подразумевает полный отказ от материального комфорта. Те, кто исповедует имперсонализм, довольствуются освобождением из материального окружения, а те, кто предан Господу, могут не только легко отказаться от мирской жизни, но и наслаждаться трансцендентным блаженством, слушая об удивительных деяниях Господа Кришны и воспевая их.")]),v._v(" "),p("p",[v._v("В Одиннадцатой песни «Шримад-Бхагаватам» (20.34) Господь Кришна говорит Уддхаве: «Дорогой Уддхава, преданные, целиком посвятившие себя служению Мне, настолько утвердились в нем, что не имеют никаких других желаний. Если им предложить даже все четыре духовных богатства, не говоря уже о любых богатствах материального мира, они откажутся от них». Нечто подобное Господь Кришна говорит и в другом месте «Бхагаватам» (11.14.14): «Дорогой Уддхава, тот, чье сознание поглощено мыслями и деятельностью, связанны-ми со Мной, не стремится занять даже положение Брахмы или стать Индрой или владыкой всех планет вселенной; его не привлекают ни восемь мистических совершенств, ни дажео свобождение». В Двенадцатой песни «Шримад-Бхагаватам» (10.6) Господь Шивао бращается к Деви: «Дорогая Деви, этот великий брахман-мудрец Маркандея обрел непоколебимую веру в Верховную Личность Бога; он всецело предан Всевышнему и потому не ищет никакой награды, включая освобождение из материального мира»")]),v._v(" "),p("p",[v._v("Похожее утверждение есть и в «Падма-пуране», где описываются ритуалы, совершаемые в месяц карттика (октябрь-ноябрь). В течение этого месяца во Вриндаване преданные ежедневно возносят молитву Господу Кришне в облике Дамодары. Дамодара — это маленький Кришна, которого матушка Яшода связала веревкой. Дама означает «веревки», а удара — «живот». Рассерженная шалостями Кришны, Яшода обвязала Его поперек живота веревкой, и с тех пор Кришну называют Дамодарой. В течение месяца карттика Дамодаре возносят такую молитву: «О Господь, Ты Владыка всего сущего, дающий все благословения». Есть много полубогов — Господь Брахма, Господь Шива и другие, — и они тоже порой исполняют желания своих преданных. Так, Господь Шива осыпал благословениями Равану, а Господь Брахма благословил Хираньякашипу. Но сами Господь Шива и Господь Брахма зависят от благословений Господа Кришны, и потому Кришну называют повелителем всех, кто дарует блага. Господь Кришна может дать Своим слугам все, чего они желают, однако, несмотря на это, в молитве преданного дальше говорится: «Я непрошу у Тебя ни материальных благ, ни даже освобождения. Единственное, чего я хочу, — это чтобы Ты даровал мне способность всегда помнить о Твоем образе Дамодары, в котором я вижу Тебя сейчас. Ты так прекрасен и привлекателен, что в моем уме не осталось никаких других желаний, кроме желания созерцать этот дивный образ». В той же молитве есть и такие строки: «О Господь Дамодара, однажды Ты напроказничал в доме Махараджи Нанды, разбив горшок с йогуртом. За эту провинность матушка Яшода решила наказать Тебя, привязав веревкой к ступе. Тогда Ты освободил сыновей Куверы — Налакувару и Манигриву, — которые в образе двух деревьев арджуна стояли во дворе")]),v._v(" "),p("p",[v._v("Махараджи Нанды. Единственное, о чем я прошу Тебя, это чтобы Ты освободил меня так же, как их, — Своими милостивыми играми».")]),v._v(" "),p("p",[v._v("С этим стихом связана такая история: двое сыновей Куверы (хранителя сокровищ полубогов) возгордились — богатства отца заставили их забыть о приличиях. Однажды они развлекались в озере на райской планете с несколькими обнаженными небожительницами. В это время мимо проходил великий святой Нарада Муни, и его очень огорчило поведение сыновей Куверы. Увидев проходившего Нараду, юные обитательницы райской планеты прикрыли свою наготу одеждами, но братья, опьяненные вином, не соблюли даже этих приличий.Рассерженный Нарада проклял их: «Вы лишились рассудка, поэтому вам больше подобает быть деревьями, чем сыновьями Куверы». Услышав это, юноши сразу опомнились и стали умолять Нараду простить их. Тогда Нарада сказал: «Вы превратитесь в деревья арджуна и будете стоять во дворе дома Махараджи Нанды. Но в назначенный срок Сам Кришна станет приемным сыном Нанды и освободит вас». Иначе говоря, проклятие Нарады обернулось благословением для сыновей Куверы, так как он косвенно предсказал, что они смогут получить милость Господа Кришны. После этого сыновья Куверы стояли как два больших дерева арджуна во дворе дома Махараджи Нанды, пока Господь Дамодара, во исполнение желания Нарады, не потащил за Собой ступу, к которой был привязан. Зацепившись ею за деревья, Он сильным рывком повалил их. Из рухнувших деревьев вышли Нала-кувара и Манигрива, ставшие с этого момента великими преданными Господа.")]),v._v(" "),p("p",[v._v("В «Хаяширша-панчаратре» есть такие строки: «О мой Господь, о Верховная Личность Бога, я не хочу никаких наград за свою праведность, не надо мне ни материального процветания, ни чувственных наслаждений, ни освобождения. Я молю Тебя только о том, чтобы вечно служить Тебе, пребывая у Твоих лотосных стоп. Смилуйся надо мной и даруй мне это благословение».")]),v._v(" "),p("p",[v._v("В той же «Хаяширша-панчаратре» описывается, как Нрисимхадева хотел вознаградить Махараджу Прахладу, но тот не принял от Него никаких материальных даров и попросил у Господа только благословения всегда оставаться Его преданным. Махараджа Прахлада сослался на вечного слугу Господа Рамачандры — Ханумана, который, подавая пример всем преданным, никогда не просил у Господа материальных благ. Он всегда был занят только служением Ему. Таково было великое бескорыстие Ханумана, за которое его по сей день почитают все преданные. Махараджа Прахлада тоже выразил ему почтение. Хануману принадлежит знаменитое высказывание: «О мой Господь, если будет на то Твоя воля, Ты можешь дать мне освобождение от материального существования или возможность слиться с Твоим бытием, но мне этого не нужно. Даже послео свобождения я не хочу ничего, что помешало бы мне служить Тебе как моему господину».")]),v._v(" "),p("p",[v._v("В похожем отрывке из «Нарада-панчаратры» говорится: «О Господь, я не хочу ничего из того, что можно получить, совершая религиозные обряды, улучшая свое материальное положение, потворствуя своим чувствам или обретая освобождение. Я молю Тебя лишь о том, чтобы Ты милостиво позволил мне находиться под сенью Своих лотосных стоп. Я не хочу ни салокь и (жить на Твоей планете), ни сарупьи (иметь такую же внешность, как у Тебя), ни других форм освобождения, а молю только даровать мне возможность всегда с любовью служить Тебе».")]),v._v(" "),p("p",[v._v("В Шестой песни «Шримад-Бхагаватам» (14.4-6) Махараджа Парикшит спрашивает Шукадеву Госвами: «О брахман, я понимаю, что Вритрасура был великим грешником и его ум целиком находился под влиянием гун страсти и невежества. Как же он сумел достичь такого совершенства в преданном служении Нараяне? Я слышал, что даже великие мудрецы, которые, подвергнув себя суровой аскезе, обрели освобождение и полное знание, должны прилагать огромные усилия, чтобы стать преданными Господа. Ясно, что такие души встречаются очень редко, поэтому мне кажется поразительным, что Вритрасура стал таким преданным!»")]),v._v(" "),p("p",[v._v("Самое важное здесь то, что освобожденных душ, слившихся с бытием безличного Брахмана, много, но преданных Верховной Личности Бога, Нараяны, можно встретить очень и очень редко. Из миллионов людей, обретших освобождение, только кому-то одному выпадает удача стать преданным.")]),v._v(" "),p("p",[v._v("В Первой песни «Шримад-Бхагаватам» (8.20) царица Кунти обращается к уезжающему Господу Кришне с такой молитвой: «Дорогой Кришна, Ты настолько велик, что даже выдающиеся ученые и парамахамсы [полностью освобожденные души] не могут постичь Тебя. Если даже эти великие мудрецы, свободные от влияния материальной энергии, неспособны познать Тебя, то что говорить о нас, женщинах, существах менее разумных? Способны ли мы познать Твое величие? Как нам постичь Тебя?» В этом стихе следует обратить внимание на то, что постичь Личность Бога могут не великие освобожденные души, а только Его преданные, подобные царице Кунти в ее смирении. Несмотря на то что она была женщиной — а женщин обычно считают менее разумными, чем мужчин, — она сознавала величие Кришны. В этом смысл данного стиха.")]),v._v(" "),p("p",[v._v("Другой очень важный стих из Первой песни «Шримад-Бхагава-там» (7.10) называется шлокой атмарама. В шлоке атмарама говорится, что трансцендентные качества Господа Кришны привлекают даже тех, кто полностью очистился от материальной скверны ( УШЧ 15 глава) . Смысл этого стихав том, что у освобожденной души полностью отсутствует стремление к материальному наслаждению, но даже такие души, хотя у них нет никаких материальных желаний, тем не менее не могут устоять против желания постичь игры Господа и слушать рассказы о них. Отсюда следует, что величие Господа и Его игры не материальны. Иначе как бы они могли привлекать атмарам, тех, кто обрел освобождение? Это главное, что нужно понять из данного стиха.")]),v._v(" "),p("p",[v._v("Из всего сказанного явствует, что преданные не стремятся ни к какому виду освобождения. Есть пять видов освобождения, уже упоминавшихся ранее: 1) слиться воедино с Господом; 2) жить на одной планете с Господом; 3) обрести тело, подобное телу Господа; 4) обладать теми же богатствами, что и Господь; 5) иметь возможность постоянно общаться с Господом. Из этих пяти видов освобождения тот, что называется саюджьей (слияние с бытием Господа), меньше всего привлекает преданного. Другие четыре, хотя и не вызывают у него желания достичь их, не противоречат идеалам преданного. Некоторые из тех, кто обрел одну из этих четырех форм освобождения, также могут развить в себе привязанность к Кришне и попасть на Голоку Вриндавану в духовном небе. Иначе говоря, те, кто уже обрел какой-нибудь из четырех видов освобождения и поднялся на планеты-Вайкунтхи, в некоторых случаях тоже могут развить в себе привязанность к Кришне и попасть на Кришналоку.")]),v._v(" "),p("p",[v._v("Таким образом, тс, кто получил какой-либо из четырех видов освобождения, могут продолжать свою эволюцию, проходя через различные стадии существования. Вначале они могут хотеть обрести богатства Кришны, но на зрелой стадии дремлющая в них любовь к Кришне, которая проявляется во Вриндаване, пробуждается в их сердце. Для чистых преданных неприемлемо освобождение саюджья, слияние со Всевышним, однако остальные четыре вида освобождения они иногда могут принимать ради служения Кришне.")]),v._v(" "),p("p",[v._v("Есть много категорий преданных Верховной Личности Бога, и самыми совершенными преданными считаются те, кого привлекает изначальный образ Господа — Кришна во Вриндаване. Таких преданных никогда не привлекают богатства Вайкунтхи или даже Двараки, столицы царства Кришны. Шри Рупа Госвами заключает, что преданные, которых привлекают игры Господа в Гокуле (Вриндаване), являются самыми возвышенными.")]),v._v(" "),p("p",[v._v("У преданного, привязанного к определенному облику Господа, не возникает желания переносить свою привязанность на другие Его облики. К примеру, преданный Господа Рамачандры Хануман знал, что между Господом Рамачандрой и Господом Нараяной нет разницы, и тем не менее хотел служить только Господу Рамачандре, поскольку каждый преданный привязан к Господу по-своему. У Господа великое множество форм, но все же Кришна является изначальной формой. Хотя всех преданных, поклоняющихся различным формам Бога, объединяет то, что они служат Ему, тем не менее считается, что преданные Господа Кришны возглавляют список преданных.")])])}),[],!1,null,null,null);_.default=e.exports}}]);