(window.webpackJsonp=window.webpackJsonp||[]).push([[37],{411:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"не-принимать-недостоиных-учеников-не-сооружать-много-храмов-и-не-читать-много-книг"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#не-принимать-недостоиных-учеников-не-сооружать-много-храмов-и-не-читать-много-книг"}},[t._v("#")]),t._v(" НЕ ПРИНИМАТЬ НЕДОСТОЙНЫХ УЧЕНИКОВ, НЕ СООРУЖАТЬ МНОГО ХРАМОВ И НЕ ЧИТАТЬ МНОГО КНИГ")]),t._v(" "),s("p",[t._v("Другое ограничение гласит, что учитель может иметь много учеников, но при этом должен действовать так, чтобы не оказаться ни у кого из них в долгу за какой-то их поступок или услугу. Также не следует увлекаться строительством новых храмов или чтением разного рода книг, за исключением тех, которые помогают совершенствоваться в преданном служении. По сути дела, вполне достаточно внимательно читать «Бхагавад-гиту», «Шримад-Бхагаватам», «Учение Шри Чайтаньи» и эту книгу — «Нектар преданности», чтобы понять науку сознания Кришны. Нет нужды утруждать себя чтением других книг.")]),t._v(" "),s("p",[t._v("В Седьмой песни «Шримад-Бхагаватам» (13.8) Нарада Муни, обсуждая с Махараджей Юдхиштхирой обязанности представителей различных укладов в обществе, особо останавливается на правилах для санньяси — людей, отрекшихся от материального мира. Тому, кто дал обет отречения от мира, запрещается принимать недостойного человека в ученики. Санньяси сначала должен проверить, действительно ли кандидат в его ученики искренен в своем стремлении обрести сознание Кришны, и если нет, то не следует давать ему посвящение. Однако Господь Чайтанья по Своей беспричинной милости наказал всем истинным духовным учителям говорить о сознании Кришны повсюду. Поэтому в Движении Господа Чайтаньи даже санньяси может говорить о сознании Кришны всюду и всегда примет в ученики тех, кто по-настоящему хочет ими стать.")]),t._v(" "),s("p",[t._v("Есть еще одно соображение: не увеличивая числа учеников, невозможно распространять сознание Кришны. Поэтому, порой даже идя на риск, санньяси, принадлежащий к школе Чайтаньи Махапрабху, может принять в ученики человека, который не обладает всеми необходимыми для этого качествами. По милости истинного духовного учителя такой ученик сможет постепенно прогрессировать. Однако, если духовный учитель увеличивает количество учеников просто ради дешевых почестей, престижа, он неизбежно сойдет с пути сознания Кришны.")]),t._v(" "),s("p",[t._v("Подобно этому, истинный духовный учитель не станет читать множество книг только ради того, чтобы блеснуть эрудицией или снискать популярность, выступая с лекциями в различных местах. Всего этого необходимо избегать. Кроме того, санньяси не следует увлекаться возведением храмов. Жизнь ачарьев, принадлежащих к сампрадае Шри Чайтаньи Махапрабху, показывает, что они не увлекались сооружением храмов. Однако если кто-то сам предлагал свои услуги, тогда те же ачарьи, ранее не проявлявшие энтузиазма в этом отношении, вдохновляли такого человека на постройку дорогостоящего храма. Так, когда Махараджа Мансингх, главнокомандующий императора Акбара, предложил свою помощь Рупе Госвами, тот поручил ему воздвигнуть большой храм Говиндаджи, стоивший огромных денег.")]),t._v(" "),s("p",[t._v("Таким образом, истинный духовный учитель не должен лично брать на себя ответственность за постройку храмов, но, если у кого-то есть деньги и он хочет потратить их на служение Кришне, ачарья, следуя за Рупой Госвами, может использовать деньги преданного, чтобы построить красивый и дорогой храм для служения Господу. К сожалению, иногда случается, что человек, недостойный быть духовным учителем, обращается к богатым людям за пожертвованиями на сооружение храма. И если такой неквалифицированный духовный учитель использует эти деньги для того, чтобы жить со всеми удобствами в роскошном храме, фактически не занимаясь проповедью, это неприемлемо. Иначе говоря, духовный учитель не должен увлекаться сооружением храмов, якобы заботясь о духовном развитии людей. Его первейшей обязанностью и самой главной деятельностью должна быть проповедь. В связи с этим Шрила Бхактисиддханта Сарасвати Госвами Махараджа советовал духовному учителю издавать книги. Если у кого-то есть деньги, то, вместо того чтобы возводить дорогостоящие храмы, он должен тратить эти деньги на издание авторитетных книг на разных языках, чтобы расширять Движение сознания Кришны.")])])}),[],!1,null,null,null);e.default=r.exports}}]);