(window.webpackJsonp=window.webpackJsonp||[]).push([[55],{492:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"санкиртана"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#санкиртана"}},[t._v("#")]),t._v(" САНКИРТАНА")]),t._v(" "),s("p",[t._v("Когда человек громко воспевает деяния Господа, Его качества, облик и т.д., это называется санкиртаной. Санкиртана означает также совместное пение святого имени Господа.")]),t._v(" "),s("p",[t._v('В «Вишну-дхарме» восхваляется совместное пение святых имен: «О царь, слово "Кришна" обладает такой силой, что всякий, кто поет это святое имя, немедленно освобождается от последствий греховных поступков, накопленных им за много, много жизней». Это факт. В «Чайтанья-чаритамрите» сказано: «Лишь раз произнеся святое имя Кришны, человек может нейтрализовать больше греховных поступков, чем он способен совершить». Грешник может совершить очень много грехов, но никто не способен совершить их столько, чтобы имя Кришна, произнесенное один раз, не смогло бы их уничтожить.')]),t._v(" "),s("p",[t._v("В Седьмой песни «Шримад-Бхагаватам» (9.18) Махараджа Прахлада обращается к Господу с такой молитвой: «О Господь Нрисимха, достигнув положения Твоего слуги, я получу возможность слушать о Твоих деяниях. Ты лучший друг и высший объект поклонения. Твои игры трансцендентны. Просто слушая о них, человек может очиститься от всех грехов. Поэтому мне незачем беспокоиться о своих прошлых грехах: просто слушая о Твоих играх, я освобожусь от всей скверны материальных привязанностей».")]),t._v(" "),s("p",[t._v("Есть много песней, посвященных деяниям Господа. Среди них и «Брахма-самхита», пропетая Господом Брахмой, и «Нарада-панчаратра», пропетая Нарадой Муни, и «Шримад-Бхагаватам»» пропетый Шукадевой Госвами. Любой, кто услышит эти песни, легко сможет вырваться из плена материальной скверны. Ничто не должно мешать нам слушать эти песни о Боге. Они передаются из уст в уста в течение многих и многих миллионов лет, и люди до сих пор получают от них благо. Так почему бы современному человеку не воспользоваться этой возможностью обрести освобождение?")]),t._v(" "),s("p",[t._v("В Первой песни «Шримад-Бхагаватам» (5.22) Нарада Муни говорит своему ученику Вьясадеве: «Дорогой Вьяса, тебе должно быть известно, что люди, совершающие аскетические подвиги, изучающие Веды, проводящие пышные обряды жертвоприношений, поющие ведические гимны, размышляющие о трансцендентном знании и щедро раздающие милостыню, за все эти благочестивые дела просто удостаиваются места в обществе преданных и получают возможность прославлять Господа». Иными словами, здесь утверждается, что прославлять Господа, воспевать Его — высшая форма деятельности живого существа.")])])}),[],!1,null,null,null);e.default=r.exports}}]);