(window.webpackJsonp=window.webpackJsonp||[]).push([[61],{433:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"вдыхание-аромата-благовонии-и-цветов-предложенных-божеству"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#вдыхание-аромата-благовонии-и-цветов-предложенных-божеству"}},[t._v("#")]),t._v(" ВДЫХАНИЕ АРОМАТА БЛАГОВОНИЙ И ЦВЕТОВ, ПРЕДЛОЖЕННЫХ БОЖЕСТВУ")]),t._v(" "),s("p",[t._v("В «Хари-бхакти-судходае» говорится о благовониях, которые предлагают в храме: «Вдыхая аромат предложенных Божеству благовоний, преданные исцеляются от яда материальной скверны, подобно тому как человек, укушенный змеей, исцеляется, вдыхая запах лечебных трав». В джунглях растут лечебные травы, и те, кто в них разбирается, могут, используя их, привести в сознание человека, укушенного змеей. Просто вдыхая запах этих трав, он избавляется от последствий укуса змеи. Точно так же, приходя в храм и вдыхая аромат благовоний, предложенных Божеству, человек полностью исцеляется от материального осквернения.")]),t._v(" "),s("p",[t._v("Любой преданный, посещая храм, должен приносить что-то для Божеств: фрукты, цветы, благовония и т.д. Если он не в состоянии делать пожертвования деньгами, то должен дарить что-нибудь другое. В Индии этот обычай строго соблюдается: и женщины, и мужчины, которые приходят утром в храм, приносят с собой кто что может. Даже пригоршня риса или муки может стать подношением. Это одно из правил преданного служения: никто не должен приходить к святому человеку или в храм с пустыми руками. Подношение может быть очень скромным или очень дорогим, но обязательно нужно предложить хотя бы цветок, плод, немного воды — то, что нам по средствам. Приходя утром предложить что-нибудь Божеству, преданный, разумеется, будет вдыхать приятный запах благовоний и тем самым очищаться от ядовитых последствий материального существования.")]),t._v(" "),s("p",[t._v("В тантра-шастре говорится: «Как только запах гирлянды, поднесенной Божеству, входит в ноздри человека, тот освобождается от пут греха. А если человек безгрешен, то, вдыхая аромат предложенных цветов, он сможет превратиться из майявади [имперсоналиста] в преданного». Тому есть несколько примеров, и самый известный из них — возвышение четырех Кумаров. Они были имперсоналистами - майявади, но, вдохнув аромат цветов и благовоний, предложенных Господу, стали преданными. Из вышеупомянутого стиха следует, что майявади (имперсоналисты) до какой-то степени осквернены. Они не чисты.")]),t._v(" "),s("p",[t._v("«Шримад-Бхагаватам» подтверждает это: «Пока человек не смыл с себя всех последствий своих грехов, он не может стать чистым преданным. У чистого преданного не осталось никаких сомнений в верховенстве Личности Бога, и потому он всегда находится в сознании Кришны и занимается преданным служением». Похожее утверждение есть в «Агастья-самхите»: чтобы очистить ноздри от любых загрязнений, нужно стараться вдыхать аромат цветов, предложенных Кришне в храме.")])])}),[],!1,null,null,null);e.default=r.exports}}]);