(window.webpackJsonp=window.webpackJsonp||[]).push([[73],{445:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"безраздельное-вручение-себя-господу"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#безраздельное-вручение-себя-господу"}},[t._v("#")]),t._v(" БЕЗРАЗДЕЛЬНОЕ ВРУЧЕНИЕ СЕБЯ ГОСПОДУ")]),t._v(" "),s("p",[t._v("Полное вручение себя Господу хорошо описано в Одиннадцатой песни «Шримад-Бхагаватам» (29,34). Господь говорит: «Того, кто полностью предался Мне и полностью отказался от всякой другой деятельности, Я беру под Свое покровительство и защищаю его как в этой жизни, так и в следующей. Иными словами, Я Сам веду его к духовным высотам. Такого человека можно считать уже достигшим саршти [то есть обладающим богатствами, равными богатствам Всевышнего]». В «Бхагавад-гите» тоже утверждается, что, как только человек предается лотосным стопам Кришны, Кришна берет на Себя заботу о нем и гарантирует ему защиту от всех последствий его грехов. Кроме того, Он наставляет такого человека изнутри, помогая ему быстро достичь духовного совершенства.")]),t._v(" "),s("p",[t._v("Такое вручение себя Всевышнему называется атма-ниведана. Знатоки шастр по-разному толкуют слово атма. Считается, что иногда это слово относится к духовному «я», то есть душе, а иногда — к уму или телу. Поэтому полностью вручить себя Господу — значит посвятить служению Ему не только себя как душу, но и свои ум и тело. Шрила Бхактивинода Тхакур написал об этом замечательную песню, в которой он целиком отдает себя в распоряжение Господа: «Мой ум, мои семейные дела, мое тело, все, чем я владею, я отдаю Тебе, посвящаю служению Тебе, о мой Господь. Теперь Ты можешь делать с этим все, что пожелаешь. Ты верховный владыка всего сущего, поэтому Ты можешь убить меня, если захочешь, а можешь оградить меня от всех опасностей. Всё в Твоей власти. Мне ничего не принадлежит».")]),t._v(" "),s("p",[t._v("Шри Ямуначарья в своих молитвах Господу выразил примерно ту же мысль: «Мой дорогой Господь, я могу находиться в теле человека или в теле полубога, но для меня это не имеет значения, ибо любое тело — всего лишь порождение трех гун материальной природы, а я, владелец этих тел, вручаю себя Тебе».")]),t._v(" "),s("p",[t._v("О том, как можно вручить Господу свое тело, сказано в «Хари-бхакти-вивеке». Преданный говорит: «О мой Господь, проданному животному не надо заботиться о своих нуждах; так и я, вручив Тебе свои тело и душу, больше не беспокоюсь о поддержании своего существования». Иначе говоря, преданный не должен тревожиться ни о том, как прокормиться самому, ни о том, как содержать свою семью. Если человек действительно и телом, и душой предался Богу, он всегда должен помнить, что ему нужно беспокоиться только о том, как служить Господу.")]),t._v(" "),s("p",[t._v("Шрила Рупа Госвами говорит, что преданное служение в дружеских отношениях и преданное служение с полным вручением себя Господу — это два сложных метода, поэтому такие взаимоотношения с Господом встречаются крайне редко. Только возвышенные преданные легко могут применять эти два метода. Таким образом, вручение себя Господу в сочетании с искренним экстатическим служением Ему встречается очень редко. Это предполагает, что человек полностью отдает себя на волю Господа.")])])}),[],!1,null,null,null);e.default=r.exports}}]);