(window.webpackJsonp=window.webpackJsonp||[]).push([[88],{460:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"повторение-святого-имени-господа"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#повторение-святого-имени-господа"}},[t._v("#")]),t._v(" ПОВТОРЕНИЕ СВЯТОГО ИМЕНИ ГОСПОДА")]),t._v(" "),s("p",[t._v("Важность повторения Харе Кришна, Харе Кришна, Кришна Кришна, Харе Харе / Харе Рама, Харе Рама, Рама Рама, Харе Харе особенно подчеркивается во Второй песни «Шримад-Бхагаватам» (1.11), где Шукадева Госвами говорит Махарадже Парикшиту: «О царь, того, кто испытывает спонтанную привязанность к повторению маха-мантры Харе Кришна, нужно считать достигшим высшей ступени совершенства». В этом стихе отмечается, что и карми, желающие наслаждаться плодами своей деятельности, и те, кто ищет спасения в слиянии с Верховной Личностью, и йоги, стремящиеся к мистическим совершенствам, могут достичь успеха, просто повторяя маха-мантру. Шукадева использует слово нирнитам, которое означает «это уже решено». Он был освобожденной душой и потому не принял бы каких-то заключений, не являющихся окончательными. Итак — Шукадева Госвами особо подчеркивает это, — не может быть никаких сомнений в том, что для человека, который повторяет маха-мантру Харе Кришна, причем делает это с решимостью и постоянством, все испытания, связанные с кармической деятельностью, умозрительным философствованием и мистической йогой, остались позади.")]),t._v(" "),s("p",[t._v("То же самое утверждает и Кришна в «Ади-пуране». Обращаясь к Арджуне, Он говорит: «Тот, кто повторяет Мое трансцендентное имя, всегда общается со Мной. И Я могу честно признаться тебе, что такому преданному совсем нетрудно приобрести Меня».")]),t._v(" "),s("p",[t._v("А в «Падма-пуране» говорится: «Мантру Харе Кришна можно услышать только из уст человека, который в течение многих жизней поклонялся Васудеве». И далее: «Святое имя Господа неотлично от Самого Господа. Из этого следует, что святое имя по самодостаточности, чистоте и вечности так же совершенно, как Сам Господь. Святое имя не материальный звук, и оно нисколько не осквернено материей». Поэтому тот, кто не сумел очистить свои чувства, неспособен повторять святое имя без оскорблений. Иными словами, материальные чувства не могут должным образом участвовать в повторении святых имен, из которых состоит маха-мантра Харе Кришна. Однако, если человек начинает повторять эту мантру, он получает возможность действительно очиститься, чтобы в скором времени повторять ее без оскорблений.")]),t._v(" "),s("p",[t._v("Чайтанья Махапрабху советовал всем повторять мантру Харе Кришна хотя бы для того, чтобы очистить свои сердца от пыли. Когда сердце очистится, человек сможет по-настоящему осознать важность святого имени. Те, у кого нет желания очистить свое сердце, кто хочет сохранить все как есть, не смогут обрести трансцендентный плод повторения мантры Харе Кришна. Поэтому надо побуждать людей развивать в себе стремление служить Господу — это поможет им повторять святое имя без оскорблений. Под руководством духовного учителя человек одновременно обучается служить и повторять мантру Харе Кришна. Как только в ученике разовьется спонтанное желание служить, он сразу сможет постичь трансцендентную природу святых имен, образующих маха-мантру.")])])}),[],!1,null,null,null);e.default=r.exports}}]);