(window.webpackJsonp=window.webpackJsonp||[]).push([[91],{463:function(v,_,t){"use strict";t.r(_);var p=t(45),e=Object(p.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h1",{attrs:{id:"глава-14-качества-преданного"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#глава-14-качества-преданного"}},[v._v("#")]),v._v(" Глава 14. КАЧЕСТВА ПРЕДАННОГО")]),v._v(" "),t("p",[v._v("Некоторые эрудированные последователи Вед считают, что знание и отрешенность от всего мирского необходимы для того, чтобы подняться на уровень преданного служения. Однако на самом деле это не так. Углубление знаний и отречение от мира, помогающие человеку утвердиться в сознании Кришны, могут быть полезны вначале, но в конечном счете можно обойтись и без них, так как преданное служение не зависит ни от чего, кроме склонности или желания заниматься им. Единственное, что требуется для преданного служения, — это искренность.")]),v._v(" "),t("p",[v._v("По мнению сведущих преданных, умозрительные рассуждения и противоестественные аскезы йогической практики могут помочь человеку обрести освобождение, но при этом ожесточают его сердце. Они нисколько не способствуют прогрессу в преданном служении. Следовательно, эти методы не помогают обрести трансцендентное любовное служение Господу. В действительности только с помощью сознания Кришны, то есть самого преданного служения, можно совершенствоваться в служении Богу. Преданное служение абсолютно: оно одновременно и причина, и следствие. Бог, Верховная Личность, является причиной и следствием всего сущего, и, чтобы приблизиться к Нему, к Абсолюту, нужно встать на путь преданного служения, который также абсолютен.")]),v._v(" "),t("p",[v._v("Это подтверждает Сам Господь в «Бхагавад-гите»: «Постичь Меня можно только с помощью преданного служения». Предваряя Свои наставления в «Гите», Господь говорит Арджуне: «Я открою тебе эти тайны, ибо ты Мой преданный». Ведическое знание в конечном счете предназначено для того, чтобы человек постиг Верховного Господа, а путь, ведущий в Его царство, — это путь преданного служения. На этом сходятся все подлинные священные писания. Теоретизирующие мыслители пренебрегают преданным служением и, пытаясь превзойти всех остальных на поприще философских исследований, лишают себя возможности испытать блаженство преданности.")]),v._v(" "),t("p",[v._v("В Одиннадцатой песни «Шримад-Бхагаватам» (20.31) Кришна говорит: «Дорогой Уддхава, для тех, кто серьезно служит Мне, нет особой пользы ни в философских рассуждениях, ни в искусственном отречении от мира. Когда человек становится Моим преданным, он без дополнительных усилий обретает плоды отречения от материальных наслаждений и знание, необходимое для постижения Абсолютной Истины». По этим признакам можно судить об уровне преданного служения. Преданный не может оставаться во тьме, потому что Господь оказывает ему особую милость и просвещает его изнутри.")]),v._v(" "),t("p",[v._v("Продолжая наставлять Уддхаву, Господь объясняет в Одиннадцатой песни «Шримад-Бхагаватам» (20.32-33): «Дорогой друг, все блага, которые можно получить с помощью кармической деятельности, аскезы, занятий философией, практики мистической йоги, благотворительности и других способов сделать свою жизнь счастливой, сами собой приходят к Моим преданным — тем, кто связан со Мной узами любовного служения. Эти преданные могут получить всё, что пожелают, но не хотят обладать чем-либо, не имеющим отношения к преданному служению Мне. Если преданный вдруг захочет обрести какое-то материальное благо, например попасть на райские планеты, или духовное благо, например оказаться на Вайкунтхах, по Моей беспричинной милости его желания с легкостью исполняются».")]),v._v(" "),t("p",[v._v("На самом деле тот, кто развивает в себе сознание Кришны, но при этом сохраняет некоторую привязанность к материальным наслаждениям, быстро избавится от нее, занимаясь регулируемым преданным служением под руководством истинного духовного учителя.")]),v._v(" "),t("p",[v._v("Далее Шрила Рупа Госвами советует не привязываться к материальным чувственным наслаждениям, но в то же время не отвергать ничего из того, что доставляет наслаждение и связано с Кришной. К примеру, человеку необходима пища и он хочет удовлетворить свое стремление есть что-то вкусное. Тогда — не ради удовлетворения языка, а ради удовлетворения Кришны — можно приготовить вкусные блюда и предложить их Кришне. Это и будет отречением от материального. Можно готовить вкусные блюда, но, пока они не предложены Кришне, мы не должны их есть. Если человек следует обету отвергать все, что не было предложено Кришне, это истинное отречение от мира. И такое отречение позволяет ему удовлетворять потребности своих чувств.")]),v._v(" "),t("p",[v._v("Имперсоналисты, пытающиеся избегать всего материального, иногда предаются суровой аскезе, но при этом они упускают возможность служить Господу, и потому их отречения от мира недостаточно для достижения совершенства. Известно много случаев, когда имперcоналисты, практиковавшие такое искусственное, не связанное с преданным служением, отречение, снова падали, привлеченные материальной скверной. Даже в наше время есть довольно много людей, формально отрекшихся от мира, принявших санньясу, которые на словах утверждают истинность духовного бытия и ложность материального. Так они выставляют напоказ свое искусственное отречение от материального мира. Но, поскольку они не в состоянии возвыситься до преданного служения, им не удается достичь цели и они снова начинают заниматься материальной деятельностью, посвящая себя филантропии или политике. Истории известно много так называемых санньяси, которые отвергали материальный мир, объявляя его иллюзорным, но потом возвращались в него. Причина этого в том, что они не стремились обрести прибежище у лотосных стоп Господа.")]),v._v(" "),t("p",[v._v('Не следует отвергать то, что можно использовать в служении Господу. В этом секрет преданного служения. Надо принимать все, что может найти применение в сознании Кришны и способствовать нашему совершенствованию в преданном служении. Например, для развития Движения сознания Кришны мы используем разнообразную технику: диктофоны, пишущие машинки, магнитофоны, микрофоны, самолеты. Иногда нам задают вопрос: «Почему же вы используете материальные вещи, если осуждаете прогресс материальной цивилизации?» На самом деле мы его не осуждаем. Мы только просим людей делать все, что они делают, в сознании Кришны. Это тот же самый принцип, на основании которого Кришна в "Бхагавад-гите» советует Арджуне применить свои воинские способности в преданном служении. Руководствуясь этим принципом, мы используем технику в служении Кришне. С таким отношением к Кришне — иначе говоря, в сознании Кришны— мы можем использовать все, что угодно. Если пишущую машинку можно применить для распространения сознания Кришны, мы должны воспользоваться ею. То же относится и к диктофону, и к любой другой вещи. Мы исходим из того, что Кришна — это всё. Он и причина, и следствие. Нам ничего не принадлежит. Все принадлежит Кришне и должно использоваться для служения Ему. Такова наша точка зрения.')]),v._v(" "),t("p",[v._v("Это, однако, не означает, что мы можем нарушать принципы преданного служения или пренебрегать соблюдением предписанных правил и ограничений. На начальной стадии преданною служения необходимо строго следовать всем правилам, установленным для нас духовным учителем. Принимая или отвергая что-либо, нужно всегда основываться на принципах преданного служения, а не придумывать самим, что принять, а что отвергнуть. Из этого следует вывод о необходимости духовного учителя, который представляет собой зримое проявление Кришны и руководит преданным от имени Верховной Личности Бога.")]),v._v(" "),t("p",[v._v("Духовный учитель ни в коем случае не должен увлекаться накоплением денег или увеличением числа своих последователей. С истинным духовным учителем такого никогда не произойдет. Но бывает, что человек становится духовным учителем просто по собственной инициативе, не получив для этого должных полномочий, — таким человеком может овладеть стремление к наживе или увеличению числа учеников. Это свидетельствует о том, что его преданное служение находится не на очень высоком уровне. Преданное служение того, кто увлекается такими достижениями, страдает. Поэтому необходимо строго соблюдать принципы ученической преемственности.")]),v._v(" "),t("p",[v._v("Человек, сознающий Кришну, чист по природе, и ему не нужно прибегать ни к какому иному методу очищения своих мыслей и поступков. Достигнутая им высокая ступень сознания Кришны сама по себе является гарантией того, что он обладает всеми добродетелями и соблюдает все правила и ограничения, предписываемые в системе мистической йоги. Для преданных соблюдение этих правил является само собой разумеющимся. Конкретный пример этого — не причинение вреда другим живым существам, которое считается добродетелью. Преданный по природе не склонен к насилию, поэтому ему не надо специально учиться не причинять другим вреда. Стремясь очиститься, некоторые становятся вегетарианцами, но преданный уже вегетарианец. Ему не требуется прилагать для этого дополнительные усилия или присоединяться к какому-нибудь вегетарианскому обществу. Для него естественно быть вегетарианцем.")]),v._v(" "),t("p",[v._v("Есть и много других примеров, показывающих, что преданному не нужно практиковать ничего, кроме сознания Кришны. Все добродетели полубогов развиваются в преданном сами собой. Те, кто сознательно придерживается принципов вегетарианства или непричинения вреда живому, с материальной точки зрения, возможно, обладают хорошими качествами, однако этого еще не достаточно, чтобы они стали преданными. Вегетарианец или человек, соблюдающий принцип ненасилия, совсем не обязательно является преданным. Но преданный естественным образом следует принципам вегетарианства и ненасилия. Итак, вегетарианство или непричинение вреда живому само по себе не является причиной того, что человек становится преданным.")]),v._v(" "),t("p",[v._v("В связи с этим в «Сканда-пуране» рассказывается об охотнике, который, получив наставления от Нарады Муни, превратился в великого преданного. Став совершенным преданным, он не хотел убивать даже муравьев. Увидев чудесное превращение, произошедшее с охотником под влиянием преданного служения, Парвата Муни, друг Нарады, заметил: «Дорогой охотник, в твоем нежелании убить даже муравья нет ничего удивительного. У того, кто обретает бхакти, сами собой проявляются все добродетели. Преданный никогда и никому не причиняет страданий».")]),v._v(" "),t("p",[v._v("Шри Рупа Госвами подтверждает здесь, что тот, кто занят преданным служением, без дополнительных усилий обретает чистоту сознания и физической деятельности, склонность к аскезе, спокойствие ума и так далее.")]),v._v(" "),t("p",[v._v("Шри Рупа Госвами говорит, что есть девять форм преданного служения: слушание, воспевание, памятование, служение, поклонение Божеству в храме, вознесение молитв, выполнение приказов, служение Кришне в качестве Его друга и принесение всего в жертву Ему. Каждый из этих методов преданного служения обладает такой силой, что если человек применяет хотя бы один из них, то непременно сможет достичь желанного совершенства. Например, и тот, кто привязан только к слушанию о Господе, и тот, кто привязан к воспеванию имени Господа, достигнут цели, к которой они стремятся в преданном служении. Это объясняется в «Чайтанья-чаритамрите». Занимаясь одним, двумя, тремя видами преданного служения или всеми сразу, человек в конце концов достигнет желанной цели — утвердится к преданном служении.")]),v._v(" "),t("p",[v._v("Существуют конкретные примеры того, как преданные, занимаясь каким-то одним видом служения Господу, достигали совершенства. Царь Парикшит достиг желанной цели, просто слушая «Шримад-Бхагаватам». Шyкадева Госвами достиг цели жизни, пересказывая «Шримад-Бхагаватам». Махараджа Прахлада добился успеха в преданном служении, постоянно памятуя о Господе. Лакшми, богиня процветания, достигла успеха в служении Господу, растирая Его лотосные стопы. Царь Притху добился успеха, поклоняясь Господу в храме, а Акрура — вознося Господу молитвы. Хануман добился успеха, лично служа Господу Рамачандре. К Арджуне успех пришел потому, что он был другом Кришны. И наконец, Махараджа Бали добился успеха, пожертвовав Кришне все, что у него было.")]),v._v(" "),t("p",[v._v("Есть также примеры преданных, каждый из которых занимался всеми видами преданного служения. В Девятой песни «Шримад-Бхагаватам» (4.18-20) говорится о том, как Махараджа Амбариша применял все методы преданного служения. В этих стихах Шукадева Госвами рассказывает о нем так: «Прежде всего царь Амбариша сосредоточил свой ум на лотоспых стопах Господа Кришны, а затем направил свою речь на описание игр и деяний Господа. Свои руки он занял мытьем храма Господа, уши — слушанием повествований о трансцендентных деяниях Господа, глаза — созерцанием прекрасного образа Господа в храме, а тело — общением с чистыми преданными Господа. [Общение подразумевает, что люди сидят вместе, едят вместе и так далее — при этом их тела неизбежно будут соприкасаться. Махараджа Амбариша общался только с чистыми преданными и не позволял, чтобы его тела касался кто-то другой.] Его ноздри вдыхали аромат цветов и туласи, предложенных Кришне, а язык ощущал вкус прасада Кришны [пищи, которая приготовлена специально для предложения Господу и которую, после того как Господь отведал ее, принимают преданные. Махараджа Амбариша мог предлагать Кришне изысканные блюда, потому что был царем и не имел недостатка в средствах. Он предлагал Кришне самые роскошные яства, а затем вкушал остатки как кришна-прасад. Махарадже Амбарише ничто не мешало поклоняться Господу с царской пышностью. Ему принадлежал очень красивый храм, в котором мурти Господа наряжали в богатые одежды и подносили Ему превосходную пищу. Таким образом, у Махараджи Амбариши было все необходимое для служения Господу, и он все делал в полном сознании Кришны». Имеется в виду, что мы должны идти по стопам великих преданных. Если мы не в состоянии заниматься всеми видами преданного служения, то должны стараться служить Господу хотя бы каким-то одним из этих способов, следуя примеру ачарьев прошлого. Если же мы, подобно Махарадже Амбарише, занимаемся всеми видами преданного служения, то каждый из них гарантирует нам достижение совершенства в служении Господу. Полностью посвятив себя преданному служению, человек естественным образом избавляется от привязанности к материальной скверне. А освобождение становится служанкой такого преданного. Эту мысль подтверждает Билвамангала Тхакур. Если человек разовьет в себе чистую преданность Господу, освобождение будет ходить за ним как служанка.")]),v._v(" "),t("p",[v._v("Шрила Рупа Госвами говорит, что знатоки шастр иногда называют преданное служение, основанное на выполнении различных правил, служением Господу с роскошью.")])])}),[],!1,null,null,null);_.default=e.exports}}]);