(window.webpackJsonp=window.webpackJsonp||[]).push([[93],{465:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"чувственное-влечение"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#чувственное-влечение"}},[t._v("#")]),t._v(" ЧУВСТВЕННОЕ ВЛЕЧЕНИЕ")]),t._v(" "),s("p",[t._v("Такое неутолимое желание служить Господу проявляют обитатели трансцендентной земли Враджа, и сильнее всего гопи. Любовь гопи к Кришне настолько возвышенна, что иногда, чтобы мы могли получить о ней хоть какое-то представление, ее называют «вожделением».")]),t._v(" "),s("p",[t._v('Автор «Шри Чайтанья-чаритамриты», Кавираджа Кришнадас, так объяснил разницу между вожделением и стремлением служить: «Под "вожделением" подразумевается желание удовлетворять свои чувства, а под "трансцендентным желанием" — стремление служить чувствам Господа». В материальном мире не бывает, чтобы любящий стремился лишь удовлетворять чувства объекта своей любви. В основном каждый в материальном мире хочет удовлетворять собственные чувства. Однако у гопи не было никаких других желаний, кроме желания удовлетворять чувства Господа. В материальном мире такое не встречается. Поэтому, когда знатоки священных писаний уподобляют экстатическую любовь гопи к Кришне вожделению, характерному для материального мира, не следует понимать это буквально. Это просто попытка дать нам некоторое представление о трансцендентной реальности.')]),t._v(" "),s("p",[t._v("Великие преданные вплоть до уровня Уддхавы находятся в очень близких дружеских отношениях с Господом, но даже они стремятся следовать примеру гопи. Это значит, что любовь гопи к Кришне, конечно же, не является материальным вожделением. А иначе с какой стати Уддхава захотел бы идти по их стопам? Другой пример — Сам Господь Чайтанья. Приняв санньясу, Он тщательно избегал общения с женщинами, но тем не менее учил, что нет лучшего способа поклонения Кришне, чем тот, который избрали гопи. Таким образом, даже Шри Чайтанья Махапрабху превозносил то, как гопи, движимые, казалось бы, вожделением, поклонялись Господу. Уже одно это свидетельствует о том, что влечение гопи к Кришне абсолютно нематериально, хотя оно похоже на вожделение. Пока человек не утвердился на трансцендентном уровне, ему очень трудно понять отношения между гопи и Кришной. Поскольку они напоминают то, как общаются между собой юноши и девушки в материальном мире, их порой ошибочно принимают за обыкновенные половые отношения. К сожалению, те, кто не может понять трансцендентную природу любовных отношений между гопи и Кришной, считают само собой разумеющимся, что это мирские отношения, и потому иногда позволяют себе рисовать непристойные картинки в современной манере.")]),t._v(" "),s("p",[t._v("С другой стороны, чувство Кубджи, которая возжелала Кришну, знатоки шастр называют «почти вожделением». Кубджа была горбуньей, которая тоже испытала прилив экстатической любви к Кришне. Однако ее влечение к Кришне было сродни мирскому, поэтому ее любовь нельзя приравнивать к любви гопи. Ее любовное отношение к Кришне называют кама-праей, то есть подобием любви гопи к Кришне.")])])}),[],!1,null,null,null);e.default=r.exports}}]);