(window.webpackJsonp=window.webpackJsonp||[]).push([[97],{469:function(t,e,s){"use strict";s.r(e);var n=s(45),r=Object(n.a)({},(function(){var t=this,e=t.$createElement,s=t._self._c||e;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"супружеская-любовь"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#супружеская-любовь"}},[t._v("#")]),t._v(" СУПРУЖЕСКАЯ ЛЮБОВЬ")]),t._v(" "),s("p",[t._v("Преданное служение тех, кто следует примеру гопи Вриндавана или цариц Двараки, называют преданным служением в супружеской любви. Преданное служение в супружеской любви может относиться к одной из двух категорий: косвенным супружеским отношениям или непосредственным супружеским отношениям. Но и в том и в другом случае необходимо следовать за определенной гопи, занятой таким служением на Голоке Вриндаване. Непосредственная привязанность к Верховной Личности Бога в супружеской любви на санскрите называется кели. Кели подразумевает непосредственное общение с Верховной Личностью Бога. Есть и такие преданные, которые не хотят непосредственно общаться с Верховной Личностью, а предпочитают наслаждаться любовными отношениями Господа с гопи, просто слушая о них.")]),t._v(" "),s("p",[t._v("Обрести супружескую любовь к Господу могут лишь те, кто следует правилам преданного служения, особенно те, кто поклоняется Радхе и Кришне в храме. В таких преданных постепенно развивается спонтанная любовь к Божествам в храме, и, слушая о любовных отношениях Господа с гопи, они со временем начинают ощущать привязанность к этим играм. Когда же эта спонтанная привязанность становится очень глубокой, преданный получает право заниматься служением, относящимся к одной из упомянутых выше категорий.")]),t._v(" "),s("p",[t._v("Супружескую любовь к Кришне могут развить в себе не только женщины. Материальное тело не имеет никакого отношения к духовной любви. В женщине может развиться стремление стать другом Кришны, а в мужчине — желание стать гопи во Вриндаване. Пример того, как преданные в облике мужчин могут захотеть стать гопи, приводится в «Падма-пуране». Некогда в Дандакаранье обитало множество мудрецов. Дандакаранья — это название леса, в котором жил Господь Рамачандра, после того как отец отправил Его в изгнание на четырнадцать лет. В то время там жило много великих мудрецов. Их пленила красота Господа Рамачандры, и они захотели стать женщинами, чтобы получить возможность обнимать Господа. Позже, когда Кришна явился в Гокуле Вриндаване, эти мудрецы родились там как гопи и, став подругами Кришны, достигли совершенства в духовной жизни.")]),t._v(" "),s("p",[t._v("Историю мудрецов Дандакараньи можно объяснить следующим образом. Когда Господь Рамачандра жил и Дандакаранье, мудрецов, занимавшихся там преданным служением, пленила Его красота, и они сразу подумали о гопи Вриндавана, наслаждающихся супружеской любовью с Кришной. Мудрецы Дандакараньи захотели вступить с Господом в супружеские отношения, подобные тем, в которых с Ним находятся гопи. Мудрецам было прекрасно известно, что и Кришна, и Рамачандра — это Верховный Господь, но Рамачандра является идеальным царем, поэтому Он не может иметь больше одной жены, тогда как Господь Кришна, обладая всей полнотой Личности Бога, может осуществить во Вриндаване все их желания. Кроме того, мудрецы пришли к заключению, что Господь Кришна привлекательнее Господа Рамачандры, и потому обратились к Рамачандре с молитвами, прося у Него возможности в следующей жизни стать гопи и общаться с Кришной. Господь Рамачандра ничего не ответил, но Его молчание свидетельствовало о том, что Он внял молитвам мудрецов. Так Господь Рамачандра благословил их на общение с Господом Кришной в будущей жизни. В результате они родились девочками из лон гопи Гокулы и осуществили свое желание наслаждаться обществом Господа Кришны, жившего тогда в Гокуле Вриндаване. Так, развив в себе трансцендентное желание вкусить супружеских отношений с Господом Кришной, они достигли высшего совершенства, какое только доступно в человеческой форме жизни.")]),t._v(" "),s("p",[t._v("Есть два вида супружеских отношений: отношения между мужем и женой и отношения между влюбленными. Те, у кого развивается любовь к Кришне как к своему мужу, попадают в Двараку, где становятся царицами, женами Господа. Те же, в ком развиваются чувства к Кришне, которые испытывает возлюбленная к своему любовнику, попадают на Голоку Вриндавану, чтобы общаться там с гопи и наслаждаться любовными отношениями с Кришной. Однако нужно помнить, что супружеская любовь к Кришне, свойственная гопи или царицам Двараки, может развиться не только в женщине, но и в мужчине. Об этом свидетельствует пример мудрецов Дандакараньи. Тот, кто хочет супружеской любви к Кришне, но не следует по стопам гопи, получает возможность общаться с Господом в Двараке.")]),t._v(" "),s("p",[t._v("В «Маха-Курма-пуране» говорится: «Великие мудрецы, сыновья богов огня, желая обрести супружескую любовь к Кришне, строго соблюдали все обеты и потому в следующих жизнях получили возможность общаться с Господом — источником всего сущего, которого называют Васудевой, или Кришной. Так все они стали Его женами».")])])}),[],!1,null,null,null);e.default=r.exports}}]);